INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `author`, `photo`, `photo1`, `price`, `discount`, `category_id`) VALUES
(38, 'Lập Kế Hoạch Kinh Doanh Hiệu Quả', '', '<p>Khi bắt đầu th&agrave;nh lập doanh nghiệp hay mở rộng quy m&ocirc; hoạt động, lập ra một bản kế hoạch kinh doanh l&agrave; bước đi đầu ti&ecirc;n kh&ocirc;ng thể thiếu. Bản kế hoạch kinh doanh c&agrave;ng được chuẩn bị kỹ lưỡng v&agrave; l&ocirc;i cuốn bao nhi&ecirc;u, cơ hội ghi điểm trước c&aacute;c nh&agrave; đầu tư c&agrave;ng lớn bấy nhi&ecirc;u. Phải chăng, th&ocirc;ng qua bản kế hoạch kinh doanh, bạn muốn người đọc:</p>\r\n\r\n<p>- Đầu tư v&agrave;o một &yacute; tưởng kinh doanh mới hay một doanh nghiệp đang hoạt động?</p>\r\n\r\n<p>- Mua lại doanh nghiệp của bạn?</p>\r\n\r\n<p>- Tham gia li&ecirc;n doanh với bạn?</p>\r\n\r\n<p>- Chấp nhận đề nghị của bạn để thực hiện hợp đồng?</p>\r\n\r\n<p>- Cấp cho bạn một khoản hỗ trợ hoặc ph&ecirc; duyệt theo quy định?</p>\r\n\r\n<p>- Thuyết phục hội đồng quản trị thay đổi phương hướng hoạt động doanh nghiệp của bạn?</p>\r\n\r\n<p>Cuốn s&aacute;ch &ldquo;Lập kế hoạch kinh doanh hiệu quả&rdquo; sẽ hướng dẫn bạn c&aacute;ch để tạo ra bản kế hoạch kinh doanh thu h&uacute;t mọi tổ chức t&agrave;i ch&iacute;nh, khiến họ phải đ&aacute;p ứng mong muốn của bạn v&agrave; hỗ trợ bạn tới c&ugrave;ng trong c&ocirc;ng việc kinh doanh. Th&ocirc;ng qua cuốn s&aacute;ch, bạn sẽ biết c&aacute;ch:</p>\r\n\r\n<p>Tạo ra bản kế hoạch kinh doanh ho&agrave;n chỉnh X&acirc;y dựng chiến lược hoạt động cho doanh nghiệp.</p>\r\n\r\n<p>Đưa ra dự b&aacute;o kinh doanh s&aacute;t với thực tế.</p>\r\n\r\n<p>Nắm r&otilde; c&aacute;c th&ocirc;ng tin t&agrave;i ch&iacute;nh ảnh hưởng lớn tới doanh nghiệp.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh x&acirc;y dựng kế hoạch, việc tham khảo &yacute; kiến chuy&ecirc;n gia l&agrave; điều cần thiết nhưng bạn phải l&agrave; người đ&oacute;ng g&oacute;p ch&iacute;nh v&agrave; hiểu tường tận mỗi chi tiết c&oacute; trong đ&oacute;. H&atilde;y xem việc lập kế hoạch giống như việc truyền đạt c&acirc;u chuyện của m&igrave;nh nhằm thuyết phục người đọc đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n con đường chinh phục mục ti&ecirc;u kinh doanh.</p>\r\n\r\n<p>Bạn chỉ c&oacute; một cơ hội duy nhất để tạo ấn tượng đầu ti&ecirc;n tốt đẹp. Đừng để n&oacute; vụt mất. H&atilde;y tr&igrave;nh b&agrave;y một văn bản c&oacute; t&iacute;nh thuyết phục cao, bố cục đẹp mắt, kh&ocirc;ng mắc lỗi ch&iacute;nh tả, ngữ ph&aacute;p, bao gồm c&aacute;c vấn đề trọng t&acirc;m v&agrave; cuối c&ugrave;ng l&agrave; chứa c&aacute;c th&ocirc;ng tin bổ trợ cần thiết.</p>\r\n\r\n<p>Bằng kiến thức, kinh nghiệm của m&igrave;nh, Brian Finch - một chuy&ecirc;n gia trong lĩnh vực tư vấn lập kế hoạch kinh doanh v&agrave; quản l&yacute; t&agrave;i ch&iacute;nh - chắc chắn sẽ gi&uacute;p bạn x&acirc;y dựng th&agrave;nh c&ocirc;ng kế hoạch kinh doanh của ri&ecirc;ng m&igrave;nh.</p>\r\n', 1, 'Brian Finch', '1632382213_1629541628_lap-ke-hoach-kinh-doanh-hieu-qua.jpg', '1629691255_a.jpg', 139000, 20, 18),
(39, 'Ma Bùn Lưu Manh Và Những Câu Chuyện Khác Của Nguyễn Trí', '', '<p>Vẫn l&agrave; những c&acirc;u chuyện đậm chất Nguyễn Tr&iacute; gai g&oacute;c, ly kỳ lại vừa đơn thuần, h&oacute;m hỉnh. T&aacute;c giả cứ b&igrave;nh thản n&oacute;i về nỗi cơ cực ở những v&ugrave;ng kinh tế mới, trong những b&atilde;i v&agrave;ng khắc nghiệt, v&agrave; ngay giữa phố thị xa hoa thật giả kh&oacute; ph&acirc;n t&igrave;nh người kh&oacute; định.&nbsp;</p>\r\n\r\n<p>Vẫn l&agrave; một Nguyễn Tr&iacute; viết cho những kiếp người tứ cố v&ocirc; th&acirc;n từ giới giang hồ rừng r&uacute; cho đến giang hồ phố thị c&oacute; lối h&agrave;nh xử kỳ dị m&agrave; hảo hớn. Tất cả tạo n&ecirc;n một kh&ocirc;ng kh&iacute; truyện đậm đặc chất ri&ecirc;ng kh&ocirc;ng lẫn v&agrave;o đ&acirc;u được để gửi gắm niềm tin của t&aacute;c giả v&agrave;o ĐẠO TRỜI.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 1, 'Nguyễn Trí', '1632382204_1629541732_ma-bun-luu-manh.jpg', '1629719340_cs.jpg', 85000, 20, 21),
(40, 'Bank 4.0 - Giao dịch mọi nơi, không chỉ là ngân hàng', '', '<p>Cuốn s&aacute;ch l&agrave; phần cuối c&ugrave;ng trong loạt s&aacute;ch về ng&acirc;n h&agrave;ng, cuốn s&aacute;ch&nbsp;<strong>Bank 4.0</strong>&nbsp;kh&aacute;m ph&aacute; tương lai của c&aacute;c ng&acirc;n h&agrave;ng giữa sự ph&aacute;t triển của c&ocirc;ng nghệ v&agrave; n&ecirc;u bật những khởi đầu của cuộc c&aacute;ch mạng n&agrave;y trong thực tế.</p>\r\n\r\n<p><strong>Bank 4.0</strong>&nbsp;kh&aacute;m ph&aacute; sự chuyển đổi căn bản đ&atilde; diễn ra trong ng&agrave;nh ng&acirc;n h&agrave;ng v&agrave; dự đo&aacute;n tương lai của n&oacute;. Ng&acirc;n h&agrave;ng sẽ tr&ocirc;ng như thế n&agrave;o sau 30, 50 năm nữa? C&aacute;c ng&acirc;n h&agrave;ng tốt nhất tr&ecirc;n thế giới đang phản ứng với sự chuyển đổi n&agrave;y; nhiều cơ quan quản l&yacute; đang điều chỉnh lại c&aacute;c quy tr&igrave;nh kiểm so&aacute;t, cấp ph&eacute;p v&agrave; quy định; những c&ocirc;ng ty khởi nghiệp FinTech đang t&aacute;i x&aacute;c lập vị thế của n&oacute; đối với ng&agrave;nh ng&acirc;n h&agrave;ng hiện nay. C&aacute;c ng&acirc;n h&agrave;ng đang bị buộc phải ph&aacute;t triển c&aacute;c khả năng mới, c&ocirc;ng việc mới v&agrave; kỹ năng mới &ndash; v&agrave; đ&oacute; l&agrave; một thế giới ho&agrave;n to&agrave;n mới. Tương lai của ng&acirc;n h&agrave;ng kh&ocirc;ng nằm ở c&aacute;c cửa h&agrave;ng gi&aacute; trị, tiện &iacute;ch thanh to&aacute;n v&agrave; t&iacute;n dụng mới, n&oacute; được nh&uacute;ng trong c&aacute;c trợ l&yacute; th&ocirc;ng minh dựa tr&ecirc;n giọng n&oacute;i như Alexa v&agrave; Siri, sẵn s&agrave;ng 24/7 để gi&uacute;p con người thanh to&aacute;n, đặt chỗ, giao dịch hoặc hỏi thăm. Ng&acirc;n h&agrave;ng 4.0 c&oacute; nghĩa l&agrave; ng&acirc;n h&agrave;ng của bạn được nh&uacute;ng v&agrave;o thế giới của bạn hoặc kh&ocirc;ng.</p>\r\n\r\n<p>T&aacute;c giả Brett King đ&atilde; b&aacute;c bỏ quan niệm sai lầm về chuyển đổi số của nhiều ng&acirc;n h&agrave;ng hiện nay. C&aacute;c ng&acirc;n h&agrave;ng hiện nay chỉ đơn thuần phủ c&ocirc;ng nghệ l&ecirc;n tr&ecirc;n m&ocirc; h&igrave;nh ng&acirc;n h&agrave;ng truyền thống cũ kỹ, nghĩa l&agrave; c&aacute;c sản phẩm v&agrave; quy tr&igrave;nh về cơ bản vẫn giữ nguy&ecirc;n, chỉ cần trang bị lại cho ph&ugrave; hợp với k&ecirc;nh số. Đ&acirc;y l&agrave; tư duy sai lầm cần được thay đổi để c&aacute;c ng&acirc;n h&agrave;ng c&oacute; thể thực sự chuyển đổi số th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch&nbsp;<strong>Bank 4.0</strong>, t&aacute;c giả đưa ra rất nhiều b&agrave;i b&aacute;o của c&aacute;c chuy&ecirc;n gia trong lĩnh vực t&agrave;i ch&iacute;nh, c&ocirc;ng nghệ nhằm cung cấp c&aacute;c kiến thức cũng như th&ocirc;ng tin thực tiễn về c&aacute;c m&ocirc; h&igrave;nh ng&acirc;n h&agrave;ng chuyển đổi số th&agrave;nh c&ocirc;ng tr&ecirc;n thế giới hiện nay, qua đ&oacute; độc giả c&oacute; được c&aacute;i nh&igrave;n to&agrave;n diện về ng&agrave;nh ng&acirc;n h&agrave;ng trong thời đại 4.0.</p>\r\n\r\n<p>Một điểm th&uacute; vị của cuốn s&aacute;ch l&agrave; phần dự b&aacute;o xu hướng tương lai của ng&agrave;nh ng&acirc;n h&agrave;ng trong v&ograve;ng 20-30 năm tới. Trong đ&oacute;, &ocirc;ng cũng đưa ra c&aacute;c ti&ecirc;u ch&iacute; chấm điểm &ldquo;số h&oacute;a&rdquo; cho c&aacute;c ng&acirc;n h&agrave;ng hiện nay, từ đ&oacute; người l&agrave;m ng&acirc;n h&agrave;ng sẽ biết ch&iacute;nh x&aacute;c vị tr&iacute; của m&igrave;nh tr&ecirc;n h&agrave;nh tr&igrave;nh chuyển đổi số, đồng thời x&aacute;c định định hướng đ&uacute;ng đắn tr&ecirc;n con đường tiến tới Ng&acirc;n h&agrave;ng 4.0.</p>\r\n\r\n<p>&ldquo;Một lần nữa, Brett King lại lập n&ecirc;n kỳ t&iacute;ch với cuốn s&aacute;ch mới nhất n&agrave;y của m&igrave;nh.&nbsp;<strong>Bank 4.0</strong>&nbsp;buộc ch&uacute;ng ta phải ph&acirc;n t&iacute;ch mổ xẻ c&aacute;i bẫy chuột m&agrave; ch&uacute;ng ta vẫn quen gọi l&agrave; ng&acirc;n h&agrave;ng, x&oacute;a bỏ ho&agrave;n to&agrave;n lối tư duy cũ kỹ về kỷ nguy&ecirc;n số, đồng thời h&igrave;nh dung lại về hoạt động ng&acirc;n h&agrave;ng tới tận năm 2050 bằng c&aacute;ch tập trung v&agrave;o những nguy&ecirc;n tắc đầu ti&ecirc;n v&agrave; nhu cầu của kh&aacute;ch h&agrave;ng. Lấy v&iacute; dụ về c&aacute;c quốc gia đang ph&aacute;t triển, King đ&atilde; vẽ ra một tầm nh&igrave;n thuyết phục cho thấy hoạt động ng&acirc;n h&agrave;ng đối với c&aacute;c cư d&acirc;n số c&oacute; thể trở th&agrave;nh một chiến lược thắng lợi v&agrave; to&agrave;n diện như thế n&agrave;o.&rdquo;</p>\r\n\r\n<p>&mdash; Jennifer Tescher, Chủ tịch ki&ecirc;m CEO của Trung t&acirc;m Đổi mới c&aacute;c Dịch vụ T&agrave;i ch&iacute;nh.</p>\r\n\r\n<p><strong>+T&Aacute;C GIẢ:</strong></p>\r\n\r\n<p>Brett King l&agrave; t&aacute;c giả của những cuốn s&aacute;ch ăn kh&aacute;ch tr&ecirc;n to&agrave;n cầu, nh&agrave; b&igrave;nh luận nổi tiếng, v&agrave; diễn giả uy t&iacute;n tr&ecirc;n thế giới về tương lai của thế giới kinh doanh. &Ocirc;ng l&agrave; cố vấn cho Nh&agrave; Trắng dưới thời Tổng thống Obama, Cục Dự trữ Li&ecirc;n bang, v&agrave; Hội đồng Kinh tế Quốc gia về tương lai của ng&agrave;nh ng&acirc;n h&agrave;ng tại Mỹ, đồng thời l&agrave; cố vấn cho nhiều ch&iacute;nh phủ v&agrave; nh&agrave; lập ph&aacute;p ở khắp nơi tr&ecirc;n thế giới. Được tạp ch&iacute; Banking Exchange gọi l&agrave; &ldquo;Vua của những kẻ đột ph&aacute;&rdquo;, King được American Banker b&igrave;nh chọn l&agrave; &ldquo;Nh&agrave; đổi mới của năm&rdquo;, The Financial Brand b&igrave;nh chọn l&agrave; &ldquo;Nh&acirc;n vật c&oacute; ảnh hưởng số 1 thế giới về c&aacute;c dịch vụ t&agrave;i ch&iacute;nh&rdquo; v&agrave; Bank Innovation đề cử l&agrave; một trong mười &ldquo;thương hiệu ngầu nhất trong lĩnh vực ng&acirc;n h&agrave;ng.&rdquo;</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 1, 'Brett King', '1632381816_ca.jpg', '1629692884_r.jpg', 139000, 20, 21),
(41, 'Bộ Sách 500 Câu Chuyện Đạo Đức - Những Câu Chuyện', '', '<ul>\r\n	<li>\r\n	<p>Như ch&uacute;ng ta đ&atilde; biết truyện kể l&agrave; một phương ph&aacute;p gi&aacute;o dục tuyệt vời. Trong từng c&acirc;u chuyện, người ta thường đặt ra những t&igrave;nh huống th&uacute; vị v&agrave; đưa ra c&aacute;ch giải quyết t&igrave;nh huống đ&oacute;. Để gi&uacute;p cho ch&uacute;ng ta c&oacute; những c&acirc;u chuyện gi&aacute;o dục hay Đến với buổi tuy&ecirc;n truyền h&ocirc;m nay c&ocirc; sẽ giới thiệu tới thầy c&ocirc; v&agrave; c&aacute;c em bộ s&aacute;ch &ldquo;500 c&acirc;u chuyện đạo đức&rdquo;. Bộ s&aacute;ch của NXB trẻ in tr&ecirc;n khổ giấy 14x18,5 cm. s&aacute;ch d&agrave;y 101 trang.</p>\r\n\r\n	<p>K&iacute;nh thưa c&aacute;c thầy c&ocirc; gi&aacute;o, thưa to&agrave;n thể c&aacute;c bạn học sinh th&acirc;n mến!</p>\r\n\r\n	<p>&nbsp;Như ch&uacute;ng ta đ&atilde; biết truyện kể l&agrave; một phương ph&aacute;p gi&aacute;o dục tuyệt vời. Trong từng c&acirc;u chuyện, người ta thường đặt ra những t&igrave;nh huống th&uacute; vị v&agrave; đưa ra c&aacute;ch giải quyết t&igrave;nh huống đ&oacute;. Để gi&uacute;p cho ch&uacute;ng ta c&oacute; những c&acirc;u chuyện gi&aacute;o dục hay Đến với buổi tuy&ecirc;n truyền h&ocirc;m nay c&ocirc; sẽ giới thiệu tới thầy c&ocirc; v&agrave; c&aacute;c em bộ s&aacute;ch &ldquo;500 c&acirc;u chuyện đạo đức&rdquo;. Bộ s&aacute;ch của NXB trẻ in tr&ecirc;n khổ giấy 14x18,5 cm. s&aacute;ch d&agrave;y 101 trang.</p>\r\n\r\n	<p>&nbsp;C&aacute;c em học sinh th&acirc;n mến! bộ s&aacute;ch 500 c&acirc;u chuyện đạo đức với những nội dung như:</p>\r\n\r\n	<p>- T&igrave;nh mẫu tử: Gồm 14 c&acirc;u chuyện cảm động về t&igrave;nh mẹ con.</p>\r\n\r\n	<p>- T&igrave;nh cha: Hồm 10 c&acirc;u chuyện cổ t&iacute;ch về cha con. Người cha thường c&oacute; bề ngo&agrave;i kh&ocirc; khan nhưng t&igrave;nh cảm lại s&acirc;u đậm v&ocirc; c&ugrave;ng.</p>\r\n\r\n	<p>- T&igrave;nh thầy tr&ograve;: Gồm 11 c&acirc;u chuyện kể về t&igrave;nh cảm thầy tr&ograve; trong m&ocirc;i trường học đường.</p>\r\n\r\n	<p>- T&igrave;nh y&ecirc;u thương: Gồm 8 c&acirc;u chuyện hay trong m&ugrave;a Gi&aacute;ng sinh, m&ugrave;a của t&igrave;nh y&ecirc;u thương.</p>\r\n\r\n	<p>- T&igrave;nh th&acirc;n &aacute;i: Gồm 14 c&acirc;u chuyện kể về t&igrave;nh th&acirc;n &aacute;i giữa người với người.</p>\r\n\r\n	<p>Qua bộ s&aacute;ch n&agrave;y c&ocirc; mong rằng c&aacute;c em sẽ c&oacute; được những gi&acirc;y ph&uacute;t giải tr&iacute; bổ &iacute;ch, hỏi hỏi được những gương người tốt việc tốt trong cuộc sống.</p>\r\n\r\n	<p>Bộ s&aacute;ch hiện đang c&oacute; trong thư viện nh&agrave; trường mời thầy c&ocirc; v&agrave; c&aacute;c em t&igrave;m đọc.</p>\r\n\r\n	<p>B&agrave;i tuy&ecirc;n truyền của c&ocirc; đến đ&acirc;y l&agrave; hết hẹn gặp lại c&aacute;c em trong buổi tuy&ecirc;n truyền s&aacute;ch lần sau!</p>\r\n	</li>\r\n</ul>\r\n', 1, 'Nguyễn Hạnh - Trần Thị Thanh Nguyên', '1632382180_1629692794_bo-sach-500-cau-chuyen-dao-duc.jpg', '1629692822_q.jpg', 200000, 20, 21),
(42, 'Lịch Sử Ung Thư - Hoàng Đế Của Bách Bệnh', '', '<p><strong><em>&ldquo;Lịch sử ung thư &ndash; Ho&agrave;ng đế của b&aacute;ch bệnh&rdquo;,</em></strong>&nbsp;l&agrave; cuốn s&aacute;ch n&oacute;i về lịch sử của ung thư. Siddhartha Mukherjee đ&atilde; đưa độc giả đến với ung thư kể từ lần đầu ti&ecirc;n n&oacute; được m&ocirc; tả v&agrave;o 4.600 năm trước bởi vị thầy thuốc người Ai Cập t&ecirc;n l&agrave; Imhotep.&nbsp;</p>\r\n\r\n<p>Cuốn s&aacute;ch l&agrave; bi&ecirc;n ni&ecirc;n sử về một căn bệnh cổ xưa &ndash; một thời l&agrave; b&iacute; mật, một căn bệnh chỉ n&ecirc;n rỉ tai &ldquo;n&oacute;i thầm&rdquo; với nhau &ndash; thường được n&oacute;i một c&aacute;ch ẩn dụ th&agrave;nh một thực thể biến h&igrave;nh chết người c&oacute; khả năng len lỏi v&agrave;o tận c&aacute;c mặt ch&iacute;nh trị, khoa học, y khoa, v&agrave; thường được m&ocirc; tả chắc chắn như một bệnh dịch khủng khiếp trong thế hệ ch&uacute;ng ta.</p>\r\n\r\n<p>Cuốn s&aacute;ch nỗ lực đi v&agrave;o t&acirc;m tr&iacute; của căn bệnh bất trị n&agrave;y, để hiểu r&otilde; đặc t&iacute;nh của n&oacute;, v&agrave; để đ&aacute;nh tan huyền thoại b&iacute; ẩn về n&oacute;. Theo Mukherjee, cuốn s&aacute;ch n&agrave;y gi&uacute;p trả lời c&acirc;u hỏi mu&ocirc;n thuở của bệnh nh&acirc;n: &ldquo;T&ocirc;i sẵn s&agrave;ng tiếp tục chiến đấu, nhưng t&ocirc;i cần biết t&ocirc;i đang chiến đấu với c&aacute;i g&igrave;.&rdquo; Mục đ&iacute;ch tối thượng của Siddhartha Mukherjee l&agrave; đặt một c&acirc;u hỏi vượt ra ngo&agrave;i cuốn tiểu sử n&agrave;y: liệu ung thư c&oacute; c&ograve;n x&acirc;m chiếm t&acirc;m tr&iacute; ch&uacute;ng ta trong tương lai kh&ocirc;ng? Liệu c&oacute; thể loại bỏ ho&agrave;n to&agrave;n căn bệnh n&agrave;y ra khỏi cơ thể v&agrave; x&atilde; hội ch&uacute;ng ta kh&ocirc;ng?</p>\r\n', 1, 'Siddhartha Mukherjee', '1632382169_1629692713_ung-thu-hoang-de-cua-bach-benh.jpg', '1629692713_ccc.jpg', 220000, 20, 24),
(44, 'Cuốn Sách Khám Phá: Trời Đêm Huyền Diệu', '', '<p>Đ&atilde; biết bao lần Anna v&agrave; Elsa được ngắm bầu trời đ&ecirc;m huyền diệu ở xứ sở của m&igrave;nh. Trong cuốn s&aacute;ch n&agrave;y, độc giả sẽ được c&ugrave;ng c&aacute;c nh&acirc;n vật phim Frozen kh&aacute;m ph&aacute; bầu trời đ&ecirc;m, hiểu biết th&ecirc;m về bắc cực quang, hệ mặt trời v&agrave; c&aacute;c đề t&agrave;i thi&ecirc;n văn l&iacute; th&uacute;.<br />\r\nBộ s&aacute;ch&nbsp;<strong>Cuốn S&aacute;ch Kh&aacute;m Ph&aacute;</strong>&nbsp;gồm 5 cuốn do nh&oacute;m t&aacute;c giả Disney Learning bi&ecirc;n soạn, ph&ugrave; hợp với phương ch&acirc;m gi&aacute;o dục STEM (kiến thức khoa học gắn với thực tế). Độc giả nhỏ tuổi sẽ được tiếp cận c&aacute;c kiến thức về khoa học, vật l&iacute;, sinh học, địa l&iacute; th&ocirc;ng qua &nbsp;phần lời dễ đọc, dễ hiểu, c&ugrave;ng h&igrave;nh ảnh th&acirc;n thiện, đẹp mắt.&nbsp;<br />\r\nMỗi cuốn s&aacute;ch sẽ đưa độc giả bước v&agrave;o một chuyến phi&ecirc;u lưu trong thế giới khoa học c&ugrave;ng c&aacute;c nh&acirc;n vật trong phim Disney. Độc giả sẽ được kh&aacute;m ph&aacute; về thế giới xe cộ v&agrave; cấu tạo &ocirc; t&ocirc; c&ugrave;ng McQueen Tia Chớp v&agrave; c&aacute;c nh&acirc;n vật trong phim Cars 3; chi&ecirc;m ngưỡng bầu trời đ&ecirc;m v&agrave; c&aacute;c hiện tượng thi&ecirc;n văn c&ugrave;ng Anna v&agrave; Elsa trong phim Frozen; giải m&atilde; những nguy&ecirc;n l&iacute; vật l&iacute; b&ecirc;n trong từng m&oacute;n đồ chơi của phim Toy Story 4; t&igrave;m hiểu về những tiềm năng k&igrave; diệu của con người c&ugrave;ng Gia đ&igrave;nh Si&ecirc;u nh&acirc;n trong phim Incredibles 2; th&aacute;m hiểm đ&aacute;y đại dương c&ugrave;ng c&aacute; Dory v&agrave; nhiều sinh vật biển kh&aacute;c.<br />\r\nBộ s&aacute;ch sẽ khiến độc giả sẽ th&ecirc;m y&ecirc;u khoa học, mọi thứ kh&ocirc;ng hề kh&ocirc; khan m&agrave; đầy l&iacute; th&uacute;, dễ hiểu!<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Cuốn s&aacute;ch kh&aacute;m ph&aacute;: Trời đ&ecirc;m huyền diệu<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Cuốn s&aacute;ch kh&aacute;m ph&aacute;: Năng lực của si&ecirc;u nh&acirc;n<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Cuốn s&aacute;ch kh&aacute;m ph&aacute;: B&iacute; mật của đồ chơi<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Cuốn s&aacute;ch kh&aacute;m ph&aacute;: Khoa học về &ocirc; t&ocirc;<br />\r\n&bull;&nbsp;&nbsp; &nbsp;Cuốn s&aacute;ch kh&aacute;m ph&aacute;: Đại dương xanh thẳm</p>\r\n', 1, 'Disney Learning', '1632382102_1629542725_troi-dem-huyen-dieu.jpg', '1629692575_bbb.jpg', 65000, 10, 22),
(45, 'Bộ Sách Những Câu Chuyện Cho Con Thành Người Tử Tế (Bộ 5)', '', '<p>&ldquo;Chỉ mong con th&agrave;nh người tử tế&rdquo; l&agrave; t&acirc;m nguyện của gần như tất cả c&aacute;c bậc cha mẹ. Những c&acirc;u chuyện về t&iacute;nh lương thiện, sự khi&ecirc;m tốn, nhường nhịn, l&ograve;ng vị tha, l&ograve;ng biết ơn, t&iacute;nh si&ecirc;ng năng, ki&ecirc;n tr&igrave; trong bộ s&aacute;ch n&agrave;y sẽ dần ho&agrave;n thiện nh&acirc;n c&aacute;ch bạn đọc trẻ, để bạn lu&ocirc;n giữ được sự tử tế lương thiện từ trong t&acirc;m ngay từ l&uacute;c tuổi c&ograve;n nhỏ.</p>\r\n\r\n<p>Bộ s&aacute;ch gồm c&aacute;c tựa:</p>\r\n\r\n<p><em>Khi&ecirc;m tốn nhường nhịn</em></p>\r\n\r\n<p><em>Si&ecirc;ng năng ki&ecirc;n tr&igrave;</em></p>\r\n\r\n<p><em>L&ograve;ng biết ơn</em></p>\r\n\r\n<p><em>T&iacute;nh lương thiện</em></p>\r\n\r\n<p><em>L&ograve;ng vị tha</em></p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 1, 'Nhiều Tác Giả', '1632382092_1629542850_bo-sach-nhung-cau-chuyen-cho-con-thanh-nguoi-tu-te.jpg', '1629692526_aaa.jpg', 139000, 12, 21),
(46, 'Lịch Sử Thế Giới', '', '<p>Từ khi được viết ra bằng tiếng Đức năm 1935,&nbsp;<em>Lược sử thế giới</em>&nbsp;đ&atilde; trở th&agrave;nh cuốn s&aacute;ch lịch sử b&aacute;n chạy ở ba mươi ng&ocirc;n ngữ. Năm 2005, cuốn s&aacute;ch được xuất bản bằng tiếng Anh với phần minh họa bổ sung, v&agrave; phi&ecirc;n bản n&agrave;y b&aacute;n được hơn nửa triệu bản tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p>Như một b&agrave;i thơ về lịch sử thế giới, E. H. Gombrich lịch l&atilde;m dẫn người đọc đi từ thời kỳ Đồ đ&aacute; đến thời kỳ của năng lượng nguy&ecirc;n tử, với những biến cố lịch sử phức tạp nhất, c&aacute;c tr&agrave;o lưu tư tưởng kh&oacute; hiểu nhất, những nh&acirc;n vật lịch sử đa chiều nhất, c&aacute;c th&agrave;nh tựu lớn lao nhất của tr&iacute; tuệ con người&hellip; tất cả đều được t&aacute;c giả m&ocirc; tả v&agrave; diễn giải dễ hiểu đến bất ngờ.</p>\r\n\r\n<p>Với cuốn s&aacute;ch n&agrave;y trong tay, bạn sẽ thấy niềm vui to lớn trong việc t&igrave;m hiểu lịch sử, c&ugrave;ng v&ocirc; số khoảnh khắc qu&ecirc;n hết tất cả để say m&ecirc; đắm m&igrave;nh trong qu&aacute; v&atilde;ng của d&ograve;ng chảy lịch sử.</p>\r\n\r\n<p>&ldquo;Một c&acirc;u chuyện hấp dẫn, được tổ chức tuyệt vời v&agrave; được kể với một năng lượng v&agrave; sự tự tin v&ocirc; c&ugrave;ng cuốn h&uacute;t, tr&agrave;n ngập t&igrave;nh nh&acirc;n văn v&agrave; tinh thần h&agrave;o sảng m&agrave; h&agrave;ng ng&agrave;n người ngưỡng mộ Gombrich đ&atilde; y&ecirc;u mến trong suốt cuộc đời d&agrave;i v&agrave; đầy s&aacute;ng tạo của &ocirc;ng. Cuốn s&aacute;ch thực sự l&agrave; một điều bất ngờ đầy th&uacute; vị, kh&ocirc;ng thể n&agrave;o cưỡng lại!&rdquo;</p>\r\n\r\n<p>- Philip Pullman</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 1, 'Nam Phong tùng thư - Phạm Quỳnh chủ nhiệm', '1632382067_1629542962_lich-su-the-gioi.jpg', '1629692465_ee.jpg', 45000, 11, 24),
(47, 'Chuyện Nghề Và Chuyện Đời - Bộ 4 Cuốn', '', '<p>T&aacute;c giả l&agrave; người đ&atilde; c&oacute; hơn 30 năm kinh nghiệm l&agrave;m việc trong m&ocirc;i trường c&aacute;c c&ocirc;ng ty đa quốc gia v&agrave; c&aacute;c c&ocirc;ng ty của Việt Nam, trong đ&oacute; c&oacute; gần 20 năm l&agrave;m Quản l&yacute; Cấp cao (Tổng gi&aacute;m đốc, Ph&oacute; Tổng gi&aacute;m đốc, Gi&aacute;m đốc điều h&agrave;nh), Cố vấn Cấp cao, Chuy&ecirc;n gia Tư vấn, Đ&agrave;o tạo&hellip; cho nhiều tập đo&agrave;n, c&ocirc;ng ty lớn. T&aacute;c giả l&agrave; người s&aacute;ng lập v&agrave; hiện đang quản l&yacute; một Group tr&ecirc;n Facebook d&agrave;nh ri&ecirc;ng cho cấp Quản l&yacute; v&agrave; Chủ doanh nghiệp, c&oacute; t&ecirc;n l&agrave; &ldquo;Group Ph&aacute;t triển Doanh nghiệp Việt&rdquo;, với số lượng khoảng 27.000 th&agrave;nh vi&ecirc;n, đang tăng l&ecirc;n từng ng&agrave;y, thường xuy&ecirc;n chia sẻ online v&agrave; tổ chức c&aacute;c buổi hội thảo offline về quản l&yacute; kinh doanh cho h&agrave;ng ngh&igrave;n doanh nghiệp.<br />\r\n<br />\r\n<br />\r\nC&aacute;c b&agrave;i viết của t&aacute;c giả vừa mang t&iacute;nh h&agrave;n l&acirc;m - t&oacute;m tắt, đ&uacute;c kết, giải th&iacute;ch những kiến thức, kinh nghiệm của thế giới về quản l&yacute; kinh doanh để người đọc tham khảo, vừa mang t&iacute;nh đời thường v&igrave; t&aacute;c giả lồng v&agrave;o đ&oacute; những ph&acirc;n t&iacute;ch, diễn giải, phản biện bằng ng&ocirc;n ngữ đời thường, rất dễ hiểu. T&aacute;c giả cũng kết hợp giữa l&yacute; thuyết s&aacute;ch vở, t&agrave;i liệu do c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i viết với trải nghiệm thực tế d&agrave;y dạn của m&igrave;nh ở cả m&ocirc;i trường đa quốc gia v&agrave; doanh nghiệp Việt để so s&aacute;nh, đối chiếu, điều chỉnh cho ph&ugrave; hợp với m&ocirc;i trường v&agrave; văn h&oacute;a đặc th&ugrave; của doanh nghiệp Việt.<br />\r\n<br />\r\n<br />\r\nB&ecirc;n cạnh những b&agrave;i viết về quản l&yacute; kinh doanh l&agrave; những b&agrave;i viết về cuộc sống đời thường, với những triết l&yacute;, tư duy c&oacute; phần &ldquo;ngược đời, kh&ocirc;ng theo số đ&ocirc;ng&rdquo; của t&aacute;c giả, thường g&acirc;y cho người đọc nhiều bất ngờ v&agrave; th&uacute; vị.<br />\r\n<br />\r\n<br />\r\nV&igrave; l&agrave; tập hợp c&aacute;c b&agrave;i viết rời rạc, bao gồm cả chuyện kinh doanh (chiến lược, m&ocirc; h&igrave;nh, cơ cấu, marketing, thương hiệu, b&aacute;n h&agrave;ng, nh&acirc;n lực, t&agrave;i ch&iacute;nh, sản xuất, cung ứng, quản l&yacute; chất lượng, quản l&yacute; khủng hoảng, kiểm so&aacute;t nội bộ&hellip;) lẫn chuyện đời thường, n&ecirc;n nội dung rất đa dạng, c&oacute; khi ngẫu hứng, tr&ugrave;ng lắp, kh&ocirc;ng theo một bố cục chặt chẽ theo kiểu &ldquo;chương nọ, điều kia&rdquo;, hay được sắp xếp theo một hệ thống, tr&igrave;nh tự &ldquo;nghi&ecirc;m t&uacute;c&rdquo; như những cuốn s&aacute;ch kh&aacute;c m&agrave; nhiều người thường thấy. Người đọc c&oacute; thể t&igrave;m thấy cả l&yacute; thuyết h&agrave;n l&acirc;m, lẫn chuyện &ldquo;tầm ph&agrave;o&rdquo;, c&oacute; khi pha ch&uacute;t h&agrave;i hước nhẹ nh&agrave;ng như ch&iacute;nh t&iacute;nh c&aacute;ch của t&aacute;c giả. Điều kh&aacute;c biệt l&agrave; mỗi b&agrave;i viết đều &iacute;t nhiều thể hiện lối tư duy ngược đời, g&oacute;c nh&igrave;n kh&aacute;c thường của t&aacute;c giả - cả trong chuyện quản l&yacute;, kinh doanh, lẫn chuyện &ldquo;thế th&aacute;i nh&acirc;n t&igrave;nh&rdquo;. V&agrave; nội dung c&aacute;c b&agrave;i viết, cho d&ugrave; thuộc phạm tr&ugrave; quản l&yacute; kinh doanh hay cuộc sống đời thường, d&ugrave; nghi&ecirc;m t&uacute;c hay h&agrave;i hước, &iacute;t nhiều đều sẽ đem lại gi&aacute; trị thiết thực cho người đọc.<br />\r\n<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i giới thiệu cuốn s&aacute;ch n&agrave;y cho độc giả đọc để tham khảo g&oacute;c nh&igrave;n thực tiễn từ một nh&agrave; quản l&yacute; chuy&ecirc;n nghiệp, một người c&oacute; vốn sống v&agrave; trải nghiệm phong ph&uacute; cả trong v&agrave; ngo&agrave;i nước, nhưng lại c&oacute; phong c&aacute;ch chia sẻ, diễn đạt rất gần gũi, dễ hiểu, mang t&iacute;nh thực tế rất cao. Mời qu&yacute; vị độc giả đ&oacute;n đọc c&aacute;c cuốn s&aacute;ch kh&aacute;c trong chuỗi c&aacute;c cuốn s&aacute;ch của t&aacute;c giả sẽ lần lượt xuất bản!</p>\r\n\r\n<p><strong>S&aacute;ch&nbsp;Chuyện Nghề V&agrave; Chuyện Đời - Bộ 4 Cuốn&nbsp;của t&aacute;c giả&nbsp;Nguyễn Hữu Long, c&oacute; b&aacute;n tại Nh&agrave; s&aacute;ch online NetaBooks với ưu đ&atilde;i Bao s&aacute;ch miễn ph&iacute; v&agrave; tặng Bookmark</strong></p>\r\n', 0, 'Nguyễn Hữu Long', '1629595165_combo-chuyen-nghe-chuyen-doi.jpg', '1629692259_e.jpg', 139000, 25, 21),
(48, 'Combo Mẹ Con Sư Tử - Bồ Tát Ngàn Tay Ngàn Mắt', '', '<p><strong>1. Bồ T&aacute;t ng&agrave;n tay ng&agrave;n mắt</strong></p>\r\n\r\n<p>V&Igrave; SAO SƯ &Ocirc;NG ĐI TU?</p>\r\n\r\n<p><em>&ldquo;B&eacute; Em ng&agrave;y xưa nay l&agrave; Sư &Ocirc;ng, một thiền sư đi giảng dạy ch&aacute;nh niệm, đem b&igrave;nh an đến cho h&agrave;ng triệu người, v&agrave; đ&atilde; c&oacute; h&agrave;ng trăm ng&agrave;n đệ tử xuất gia, tại gia ở Việt Nam, T&acirc;y phương v&agrave; khắp nơi tr&ecirc;n thế giới&rdquo;&hellip;</em></p>\r\n\r\n<p>---</p>\r\n\r\n<p>Qua cuốn s&aacute;ch n&agrave;y, bạn đọc sẽ được d&otilde;i theo những c&acirc;u chuyện giản dị, trong trẻo của những em nhỏ ngoan ngo&atilde;n, th&ocirc;ng minh v&agrave; sớm c&oacute; ước mơ cao đẹp. Bạn sẽ biết được &ldquo;V&igrave; sao Sư &Ocirc;ng đi tu?&rdquo;, &ldquo;Bụt l&agrave; ai?&rdquo;, &ldquo;Bụt ở đ&acirc;u?&rdquo;, &ldquo;Bồ T&aacute;t Ng&agrave;n Tay Ng&agrave;n Mắt l&agrave; ai?&rdquo; v&agrave; nhận ra ngay trong ch&iacute;nh m&igrave;nh cũng c&oacute; nhiều phẩm chất tốt đẹp như c&aacute;c em nhỏ nơi đ&acirc;y.</p>\r\n\r\n<p>-------</p>\r\n\r\n<p><strong>2. Mẹ con sư tử</strong></p>\r\n\r\n<p>H&Atilde;Y L&Agrave; CH&Uacute; SƯ TỬ MẠNH MẼ TRONG ĐỜI!</p>\r\n\r\n<p><em>&ldquo;Sư tử mẹ nh&igrave;n sư tử con mỉm cười. N&oacute; nhảy qua b&ecirc;n kia bờ suối đi v&agrave;o rừng th&igrave; sư tử con cũng mỉm cười, nhảy theo mẹ, vượt qua suối v&agrave;o rừng. Sư tử con biết rằng, n&oacute; kh&ocirc;ng phải l&agrave; khỉ, n&oacute; l&agrave; sư tử. Từ đ&oacute; hai mẹ con được đo&agrave;n tụ hạnh ph&uacute;c&rdquo;&hellip;</em></p>\r\n\r\n<p>---</p>\r\n\r\n<p>C&acirc;u chuyện giản dị, gần gũi được Sư &Ocirc;ng TH&Iacute;CH NHẤT HẠNH kể lại như một ngụ ng&ocirc;n gi&agrave;u h&agrave;m nghĩa, khơi nguồn những điều thiện hảo trong t&acirc;m hồn.</p>\r\n\r\n<p>H&atilde;y l&agrave; ch&uacute; sư tử mạnh mẽ trong đời.</p>\r\n\r\n<p>---</p>\r\n\r\n<p>Về Thiền sư TH&Iacute;CH NHẤT HẠNH</p>\r\n\r\n<p><em>&ldquo;Th&iacute;ch Nhất Hạnh l&agrave; một trong những tiếng n&oacute;i quan trọng nhất của thời đại ch&uacute;ng ta. Hơn bao giờ hết, đ&acirc;y l&agrave; l&uacute;c ch&uacute;ng ta cần lắng nghe vị thầy n&agrave;y.&rdquo;</em>&nbsp;&ndash; Sogyal Rinpoche</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 0, 'Thích Nhất Hạnh', '1629595369_combo-me-con-su-tu-bo-tat-ngan-tay-ngan-mat.jpg', '1629692233_dd.jpg', 129000, 25, 21),
(49, 'Combo Osho: Hạnh Phúc Tại Tâm, Can Đảm Biến Thách Thức Thành Sức Mạnh & Sáng Tạo Bừng Cháy Sức Mạnh Bên Trong', '', '<p>Con người c&agrave;ng đau khổ th&igrave; c&agrave;ng kh&aacute;t khao hạnh ph&uacute;c. Hạnh ph&uacute;c l&agrave; những cảm nhận huyền diệu từ ph&iacute;a b&ecirc;n kia của khổ đau. Hạnh ph&uacute;c cũng l&agrave; cảm nhận ch&acirc;n thật v&agrave; s&acirc;u sắc của t&acirc;m hồn trước g&aacute;nh nặng cuộc đời.<br />\r\nNh&agrave; triết học hiện đại Osho, thiền sư Osho, đạo sư Osho, luận sư Osho, nh&agrave; s&aacute;ng tạo Osho, hay đơn giản l&agrave;&hellip; Osho, đ&atilde; kh&ocirc;ng ngừng n&oacute;i cho người nghe c&ugrave;ng thời v&agrave; người đọc của mọi thời về bản chất của niềm vui sống.<br />\r\nSuốt 35 năm, tư tưởng của &ocirc;ng bay khắp mọi nơi tr&ecirc;n thế giới bằng con đường truyền b&aacute; của những m&ocirc;n đệ thầm lặng, bất chấp những đ&aacute;nh gi&aacute; khắt khe v&agrave; đầy &ldquo;hoang mang&rdquo; về tinh thần triết học m&atilde;nh liệt của &ocirc;ng.<br />\r\nT&aacute;c phẩm Hạnh ph&uacute;c tại t&acirc;m (JOY: The Happiness That Comes from Within) m&agrave; bạn đang cầm tr&ecirc;n tay cũng l&agrave; một trong số những t&acirc;m t&igrave;nh th&aacute;nh thiện đ&oacute;. Osho từng n&oacute;i sứ mạng của &ocirc;ng l&agrave; tạo điều kiện cho việc ra đời một thế giới của những con người ưu t&uacute;. &Ocirc;ng thường m&ocirc; tả lo&agrave;i người mới n&agrave;y với t&ecirc;n gọi &ldquo;Zorba-Phật&rdquo; &ndash; những con người c&oacute; thể vui sống giữa trần gian m&agrave; vẫn an tịnh khoan h&ograve;a như Đức Phật vĩ đại.<br />\r\nSợi chỉ đỏ xuy&ecirc;n suốt t&aacute;c phẩm của Osho l&agrave; c&aacute;i nh&igrave;n chứa đựng sự th&ocirc;ng th&aacute;i vĩnh hằng của phương Đ&ocirc;ng kết hợp với những tiềm năng v&ocirc; tận từ khoa học c&ocirc;ng nghệ phương T&acirc;y. Những mẩu chuyện h&agrave;i hước, những ngụ ng&ocirc;n triết học, những t&acirc;m sự đầy trải nghiệm c&aacute; nh&acirc;n, những đ&uacute;c kết hiền minh&hellip; về hạnh ph&uacute;c ch&acirc;n thật l&agrave; tất cả những g&igrave; m&agrave; Osho d&agrave;nh cho người lắng nghe &ocirc;ng. To&agrave;n bộ c&aacute;c chương mục trong cuốn s&aacute;ch n&agrave;y tập trung v&agrave;o bản chất của đời sống v&agrave; khả năng hạnh ph&uacute;c của con người.<br />\r\nHạnh ph&uacute;c l&agrave; trở về với ch&iacute;nh m&igrave;nh. Mệnh đề ngắn gọn n&agrave;y c&oacute; vẻ chỉ đơn giản trong cấu tr&uacute;c ngữ ph&aacute;p hoặc chuỗi &acirc;m tiết; n&oacute; thực sự l&agrave; một mệnh đề khắc nghiệt nhất trong h&agrave;nh tr&igrave;nh sống của mỗi ch&uacute;ng ta. Cuộc trở về n&agrave;y, theo Osho, l&agrave; sự hồi sinh phần trong trẻo v&agrave; tự nhi&ecirc;n nhất của năng lượng, l&agrave; khả năng nhận biết v&agrave; y&ecirc;u mến Sự thật. Trong lập luận của Osho, t&ocirc;n gi&aacute;o cũng kh&ocirc;ng đủ chỗ cho mệnh đề ấy được sinh s&ocirc;i. L&agrave; một gi&aacute;o sư triết học, một đạo sư c&oacute; khuynh hướng dẫn dắt t&acirc;m linh, Osho đ&atilde; tổng hợp v&agrave; đưa v&agrave;o những b&agrave;i thuyết giảng của m&igrave;nh &aacute;nh s&aacute;ng của nhiều t&ocirc;n gi&aacute;o như Phật gi&aacute;o, Thi&ecirc;n Ch&uacute;a gi&aacute;o, Jain gi&aacute;o, Ấn Độ gi&aacute;o,<br />\r\n&Ocirc;ng cũng kh&ocirc;ng ngần ngại n&oacute;i đến nỗi &aacute;m ảnh tối tăm của niềm tin kh&ocirc;ng tự chủ của nhiều t&iacute;n đồ khi trở th&agrave;nh kẻ cuồng t&iacute;n. Tr&agrave;n ngập trong mỗi lời n&oacute;i của Osho l&agrave; b&oacute;ng d&aacute;ng v&agrave; chiều s&acirc;u của c&aacute;c bộ Kinh Phật, Kinh Th&aacute;nh v&agrave; suối nguồn t&acirc;m linh Ấn Độ. Mẫu h&igrave;nh Zorba - Phật m&agrave; Osho đề xướng c&oacute; thể xem như một &ldquo;ph&aacute;t kiến&rdquo; m&atilde;nh liệt về nh&acirc;n c&aacute;ch con người &ndash; một kiểu nh&acirc;n c&aacute;ch hội tụ gần hết phẩm chất của c&aacute;c&hellip; gi&aacute;o chủ như Đức Th&iacute;ch Ca, Đức Ch&uacute;a Jesus, những hiền nh&acirc;n ph&oacute;ng kho&aacute;ng v&agrave; những t&acirc;m hồn th&agrave;nh thật, tự do trước mọi r&agrave;ng buộc nh&agrave;m ch&aacute;n v&agrave; nhẫn t&acirc;m của x&atilde; hội. Nhưng cơ chế để tạo th&agrave;nh nh&acirc;n c&aacute;ch Zorba ấy, theo Osho, lại kh&ocirc;ng phải l&agrave; con đường &ldquo;dạy dỗ&rdquo; đầy &aacute;p đặt c&ugrave;ng với những kiến thức lu&ocirc;n c&oacute; nguy cơ b&oacute;p m&eacute;o khả năng sống tự do b&igrave;nh thản của con người. Tr&aacute;i lại, c&agrave;ng bu&ocirc;ng xả, c&agrave;ng hồn nhi&ecirc;n, con người c&agrave;ng c&oacute; điều kiện trở lại ch&iacute;nh m&igrave;nh v&agrave; sống h&ograve;a b&igrave;nh với thế giới, y&ecirc;n ổn h&agrave;i h&ograve;a trong nội t&acirc;m.<br />\r\n2/ Đạo con đường kh&ocirc;ng lối:<br />\r\nĐạo - Con Đường Kh&ocirc;ng Lối được ghi lại từ b&agrave;i n&oacute;i chuyện của Osho về 5 c&acirc;u chuyện ngụ ng&ocirc;n trong cuốn Liệt tử (hay Liệt Ngự Khấu, một nh&acirc;n vật Đạo gia sống v&agrave;o thế kỉ 4 TCN). Trong phần tr&igrave;nh b&agrave;y của m&igrave;nh, t&aacute;c giả Osho thổi v&agrave;o đ&oacute; những diễn giải mới mẻ của đương đại. Qua đ&oacute; &ocirc;ng muốn người nghe kh&aacute;m ph&aacute; sự đối lập thực sự giữa l&yacute; tr&iacute; - phi l&yacute; tr&iacute;, giữa t&iacute;nh dương - t&iacute;nh &acirc;m, giữa sự tu&acirc;n thủ luật lệ - sự tự nhi&ecirc;n nhi nhi&ecirc;n.<br />\r\nCuốn s&aacute;ch gồm 5 chương, c&ugrave;ng 1 phần Hỏi - đ&aacute;p. Trong phần hỏi - đ&aacute;p, với việc trả lời những thắc mắc của người nghe, t&aacute;c giả muốn chỉ ra c&aacute;ch &aacute;p dụng sự uy&ecirc;n nguy&ecirc;n của triết l&yacute; Đạo gi&aacute;o v&agrave;o cuộc sống h&agrave;ng ng&agrave;y.<br />\r\nChương Một - Ai mới l&agrave; người hạnh ph&uacute;c? l&yacute; giải l&agrave;m sao m&agrave; nỗi khổ sở lại được khởi ph&aacute;t từ sự tồn tại của c&aacute;i t&ocirc;i, của bản ng&atilde;.<br />\r\nChương Hai - Người biết c&aacute;ch tự an ủi chỉ ra một sự th&acirc;m s&acirc;u hơn trong c&aacute;ch nh&igrave;n nhận những niềm h&acirc;n hoan lu&ocirc;n lồ lộ ra b&ecirc;n ngo&agrave;i của con người.<br />\r\nChương Ba - Kh&ocirc;ng hối tiếc lại n&oacute;i về sự kh&aacute;c biệt giữa tr&iacute; tuệ c&oacute; được từ sự gom g&oacute;p từ b&ecirc;n ngo&agrave;i, đối s&aacute;nh với c&aacute;i biết khởi sinh từ b&ecirc;n trong.<br />\r\nChương Bốn - Sống th&igrave; kh&ocirc;ng c&oacute; nghỉ ngơi l&agrave; một cuộc đối thoại giữa học tr&ograve; với thầy m&igrave;nh, qua đ&oacute; sẽ hiển lộ cho ch&uacute;ng ta thấy những giới hạn của mọi ng&agrave;nh triết học lẫn hệ quả bấp b&ecirc;nh khi người ta sống chỉ v&igrave; mong cầu một kết quả trong tương lai.<br />\r\nChương Năm - Tốt nhất l&agrave; tĩnh lặng, tốt nhất l&agrave; trống rỗng luận b&agrave;n về hai con đường đến được với thượng đế tối cao. Đ&oacute; l&agrave; con đường khẳng định của Kit&ocirc; gi&aacute;o, Do Th&aacute;i gi&aacute;o v&agrave; Hồi gi&aacute;o; v&agrave; con đường phủ định (hay con đường huyền m&ocirc;n) của Phật v&agrave; L&atilde;o Tử.<br />\r\n/ S&aacute;ng tạo bừng ch&aacute;y sức mạnh b&ecirc;n trong:<br />\r\nBạn đọc Việt Nam đ&atilde; y&ecirc;u th&iacute;ch với quyển s&aacute;ch Hạnh Ph&uacute;c Tại T&acirc;m (JOY: The Happiness That Comes from Within) của t&aacute;c gia Osho do First News xuất bảnv&agrave; đ&aacute;nh gi&aacute; rất cao. Kh&ocirc;ng chỉ vậy, quyển s&aacute;ch c&ograve;n trở th&agrave;nh người bạn đồng h&agrave;nh trong cuộc sống. V&agrave; giờ đ&acirc;y, bạn đọc chắc chắn sẽ t&igrave;m được nhiều cảm hứng với quyển s&aacute;ch S&aacute;ng Tạo &ndash; Bừng Ch&aacute;y Sức Mạnh B&ecirc;n Trong (Creaty Unleashing The Fores Within).<br />\r\nTrong đời sống hiện đại, ai cũng cần c&oacute; khả năng đ&aacute;p ứng một c&aacute;ch s&aacute;ng tạo trước những thử th&aacute;ch mới. Những ai chỉ biết đối diện với cuộc đời bằng những g&igrave; đ&atilde; học trong qu&aacute; khứ th&igrave; sẽ bị thiệt th&ograve;i gh&ecirc; gớm trong c&aacute;c mối quan hệ lẫn trong sự nghiệp của m&igrave;nh. Để chuyển từ th&aacute;i độ sống bắt chước v&agrave; bị g&ograve; b&oacute; bởi c&aacute;c nguy&ecirc;n tắc sang lối sống s&aacute;ng tạo v&agrave; linh hoạt, ch&uacute;ng ta cần phải thay đổi s&acirc;u sắc th&aacute;i độ của ch&uacute;ng ta về bản th&acirc;n v&agrave; về khả năng của ch&iacute;nh m&igrave;nh.<br />\r\nS&aacute;ng tạo l&agrave; sự nổi loạn ngoạn mục nhất trong cuộc sống. Để s&aacute;ng tạo, bạn phải tho&aacute;t khỏi mọi khu&ocirc;n ph&eacute;p; bằng kh&ocirc;ng, sự s&aacute;ng tạo của bạn chỉ l&agrave; một bản sao ch&eacute;p kh&ocirc;ng hơn kh&ocirc;ng k&eacute;m. Bạn cũng chỉ c&oacute; thể s&aacute;ng tạo khi bạn tồn tại như một c&aacute; thể ri&ecirc;ng biệt, độc đ&aacute;o v&agrave; t&aacute;ch rời khỏi t&acirc;m l&yacute; đ&aacute;m đ&ocirc;ng.<br />\r\nNgười s&aacute;ng tạo kh&ocirc;ng thể bước theo lối m&ograve;n cũ kỹ. Anh ta phải vạch ra hướng đi ri&ecirc;ng cho m&igrave;nh, phải tự kh&aacute;m ph&aacute; những b&iacute; ẩn của cuộc đời. Anh ta phải dấn th&acirc;n, phải vượt ra khỏi t&acirc;m tr&iacute; của đ&aacute;m đ&ocirc;ng. T&acirc;m l&yacute; đ&aacute;m đ&ocirc;ng cũng c&oacute; sức c&aacute;m dỗ của ri&ecirc;ng n&oacute;: những ai một mực cho rằng lựa chọn của đ&aacute;m đ&ocirc;ng l&agrave; hướng đ&uacute;ng đắn duy nhất th&igrave; sẽ được t&ocirc;n trọng, k&iacute;nh nể.<br />\r\nS&aacute;ng Tạo &ndash; Bừng Ch&aacute;y Sức Mạnh B&ecirc;n Trong sẽ dẫn dắt bạn đi v&agrave;o thế giới của sự s&aacute;ng tạo từ những bước chuẩn bị ban đầu, đến những trở ngại, kh&oacute; khăn, những thắc mắc v&agrave; cuối c&ugrave;ng kh&ocirc;ng g&igrave; kh&aacute;c hơn l&agrave; sự s&aacute;ng tạo. Ở đ&oacute;, bạn nhận thấy &yacute; nghĩa cuộc sống l&agrave; một vũ điệu chứ kh&ocirc;ng phải một tảng đ&aacute; trước mặt. N&oacute; cũng ch&iacute;nh l&agrave; &acirc;m nhạc. V&agrave; bạn l&agrave; người tạo ra n&oacute; chứ kh&ocirc;ng phải l&agrave; t&igrave;m thấy n&oacute;.<br />\r\nNh&agrave; triết học hiện đại Osho, thiền sư Osho, đạo sư Osho, luận sư Osho, nh&agrave; s&aacute;ng tạo Osho, hay đơn giản l&agrave;&hellip; Osho, đ&atilde; kh&ocirc;ng ngừng n&oacute;i cho người nghe c&ugrave;ng thời v&agrave; người đọc của mọi thời về bản chất của cuộc sống. Suốt 35 năm, tư tưởng của &ocirc;ng bay khắp mọi nơi tr&ecirc;n thế giới bằng con đường truyền b&aacute; của những m&ocirc;n đệ thầm lặng, bất chấp những đ&aacute;nh gi&aacute; khắt khe v&agrave; đầy &ldquo;hoang mang&rdquo; về tinh thần triết học m&atilde;nh liệt của &ocirc;ng. T&aacute;c phẩm S&aacute;ng Tạo &ndash; Bừng Ch&aacute;y Sức Mạnh B&ecirc;n Trong cũng l&agrave; một trong số những t&acirc;m t&igrave;nh th&aacute;nh thiện đ&oacute;.<br />\r\n4/ Can Đảm Biến Th&aacute;ch Thức Th&agrave;nh Sức Mạnh:<br />\r\nCan Đảm Biến Th&aacute;ch Thức Th&agrave;nh Sức Mạnh l&agrave; cuốn s&aacute;ch kế tiếp trong bộ bốn cuốn của hiền triết Osho gồm: S&aacute;ng tạo bừng ch&aacute;y sức mạnh b&ecirc;n trong; Hạnh ph&uacute;c tại t&acirc;m; Th&acirc;n mật - cội nguồn của hạnh ph&uacute;c; Đạo - con đường kh&ocirc;ng lối.<br />\r\nQua cuốn s&aacute;ch bạn sẽ hiểu hơn về sự can đảm l&agrave; đ&aacute;nh đổi c&aacute;i đ&atilde; biết để t&igrave;m đến c&aacute;i hư v&ocirc;, đ&aacute;nh đổi c&aacute;i quen thuộc để t&igrave;m đến những điều xa lạ, đ&aacute;nh đổi sự tiện nghi ấm c&uacute;ng để t&igrave;m đến c&aacute;i bất tiện, đ&aacute;nh đổi cuộc h&agrave;nh hương gian nan để t&igrave;m đến một nơi v&ocirc; định. Kh&ocirc;ng ai biết được liệu m&igrave;nh c&oacute; l&agrave;m được hay kh&ocirc;ng cho đến khi ch&iacute;nh bạn phải bước v&agrave;o những nỗi đau, th&igrave; khi đ&oacute; bạn mới tin rằng kh&ocirc;ng c&oacute; điều g&igrave; l&agrave; giới hạn trong sự chế ngự của sức mạnh can đảm ở mỗi con người.<br />\r\nMong rằng cuốn s&aacute;ch n&agrave;y sẽ đem lại sự b&igrave;nh an, an lạc trong t&acirc;m hồn v&agrave; l&agrave; liều thuốc chữa l&agrave;nh bạn trong những vết thương l&ograve;ng, l&agrave; điểm tựa vững v&agrave;ng để bạn vượt qua mọi r&agrave;o cản, s&oacute;ng gi&oacute; vượt l&ecirc;n ch&iacute;nh m&igrave;nh, để t&igrave;m ra con đường đ&uacute;ng đ&iacute;ch m&agrave; bạn đi tới.<br />\r\n5/ Th&acirc;n Mật Cội Nguồn Của Hạnh Ph&uacute;c:<br />\r\n&quot;Mọi người đều muốn nhưng e ngại tạo n&ecirc;n sự th&acirc;n mật.<br />\r\nVấn đề c&agrave;ng trở n&ecirc;n phức tạp hơn bởi kh&ocirc;ng ai muốn xa c&aacute;ch. Mọi người đều muốn th&acirc;n mật với người kh&aacute;c bởi nếu kh&ocirc;ng, họ sẽ trở n&ecirc;n lạc l&otilde;ng giữa thế giới rộng lớn n&agrave;y - kh&ocirc;ng bạn b&egrave;, kh&ocirc;ng người thương y&ecirc;u, kh&ocirc;ng một ai để tin cậy, để cởi mở tấc l&ograve;ng v&agrave; ph&ocirc; ra những vết thương &acirc;m ỉ. Vết thương kh&ocirc;ng thể l&agrave;nh da, trừ phi bạn gỡ n&oacute; khỏi sự bưng b&iacute;t, che đậy.<br />\r\nThế nhưng, mọi người lại ngại th&acirc;n mật, bạn muốn th&acirc;n mật với người kh&aacute;c nhưng vẫn kh&ocirc;ng muốn từ bỏ &quot;chiếc &aacute;o gi&aacute;p sắt&quot; của m&igrave;nh.<br />\r\nkh&ocirc;ng ai muốn từ bỏ lớp r&agrave;o chắn bảo vệ, v&agrave; kh&ocirc;ng ai muốn cởi mở ch&acirc;n th&agrave;nh hay tuyệt đối - tuy nhi&ecirc;n cả hai đều mong mỏi sự ch&acirc;n thật.<br />\r\nDo đ&oacute;, ch&iacute;nh bạn phải khởi xướng!&#39;</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 0, 'Gosho Aoyama', '1629595461_combo-hanh-phuc-sang-tao.jpg', '1629692201_d.jpg', 340000, 25, 21),
(50, 'Combo Giáo Dục Và Ý Nghĩa Cuộc Sống Và Bạn Đang Nghịch Gì Với  Đời Mình?', '', '<p>Krishnamurti l&agrave; t&aacute;c giả của rất nhiều cuốn s&aacute;ch. Ở tuổi 90, Krishnamurti đ&atilde; diễn thuyết tại Li&ecirc;n Hợp Quốc về chủ đề h&ograve;a b&igrave;nh v&agrave; nhận thức. Được trao tặng Hu&acirc;n chương H&ograve;a b&igrave;nh của Li&ecirc;n Hợp Quốc v&agrave;o năm 1984, &ocirc;ng được xem l&agrave; một hiền nh&acirc;n, triết gia v&agrave; nh&agrave; tư tưởng đ&atilde; tạo ra &yacute; nghĩa căn bản v&agrave; mới mẻ cho t&ocirc;n gi&aacute;o bằng c&aacute;ch chỉ r&otilde; một c&aacute;ch sống vượt khỏi tất cả c&aacute;c t&ocirc;n gi&aacute;o. &Ocirc;ng can đảm đối diện với những vấn đề của x&atilde; hội v&agrave; ph&acirc;n t&iacute;ch bằng sự r&otilde; r&agrave;ng, t&iacute;nh khoa học những hoạt động của t&acirc;m tr&iacute; con người. &Ocirc;ng kh&ocirc;ng tr&igrave;nh b&agrave;y bất kỳ triết thuyết n&agrave;o, tr&aacute;i lại chỉ n&oacute;i về những sự việc c&oacute; li&ecirc;n quan đến tất cả ch&uacute;ng ta trong cuộc sống h&agrave;ng ng&agrave;y. Khi ph&aacute;t biểu rằng: nếu ta được gi&aacute;o dục chỉ để trở n&ecirc;n kh&aacute;c biệt, gi&agrave;nh lấy một c&ocirc;ng việc tốt hơn, c&oacute; nhiều năng lực hơn, thống trị người kh&aacute;c nhiều hơn, th&igrave; cuộc sống của ta sẽ trở n&ecirc;n hời hợt v&agrave; trống rỗng v&ocirc; c&ugrave;ng, J.Krishnamurti đ&atilde; tạo n&ecirc;n sự r&uacute;ng động kh&ocirc;ng chỉ ở Ấn Độ, qu&ecirc; hương &ocirc;ng, m&agrave; c&ograve;n với cả thế giới. C&agrave;ng đi s&acirc;u v&agrave;o t&aacute;c phẩm, c&agrave;ng nhận ra, Gi&aacute;o Dục V&agrave; &Yacute; Nghĩa Cuộc Sống mang đến c&acirc;u chuyện ho&agrave;n to&agrave;n mới về gi&aacute;o dục. Theo đ&oacute;, con người l&agrave; một tổng thể h&agrave;i h&ograve;a được tạo th&agrave;nh từ nhiều kh&iacute;a cạnh kh&aacute;c nhau. Nền gi&aacute;o dục phải mang đến sự hợp nhất c&aacute;c kh&iacute;a cạnh ri&ecirc;ng biệt n&agrave;y &ndash; v&igrave; nếu kh&ocirc;ng c&oacute; sự hợp nhất ấy, cuộc sống sẽ trở th&agrave;nh một chuỗi những cuộc xung đột v&agrave; nỗi bất hạnh. Muốn tạo dựng một nền gi&aacute;o dục đ&uacute;ng đắn, ta phải hiểu r&otilde; &yacute; nghĩa cuộc sống trong t&iacute;nh to&agrave;n thể của n&oacute;.</p>\r\n', 0, ' J.Krishnamurti', '1629595513_combo-giao-duc-va-y-nghia-cuoc-song.jpg', '1629692155_cc.jpg', 200000, 25, 21),
(51, 'Combo Dinh Dưỡng Xanh - Thần dược xanh', '', '<p>Combo Thần Dược Dinh Dưỡng: Ăn Xanh Để Khỏe + Dinh Dưỡng Xanh + Thần Dược Xanh + Sống L&agrave;nh Để Trẻ ( Bộ 4 cuốn) &quot;Sức khỏe l&agrave; vốn qu&yacute; của con người&quot;. H&atilde;y để 4 cuốn s&aacute;ch dưới đ&acirc;y trở th&agrave;nh &quot;người bạn dinh dưỡng&quot; mỗi ng&agrave;y của bạn nh&eacute;! 1, Ăn Xanh Để Khỏe Chất lượng thực phẩm v&agrave; sự ph&ugrave; hợp giữa thực phẩm với nhu cầu cơ thể l&agrave; hai yếu tố mang t&iacute;nh quyết định đối với cơ thể con người. Trong hai thế kỷ trở lại đ&acirc;y, ch&uacute;ng ta mới bắt đầu nhận ra ch&acirc;n l&yacute; n&agrave;y. Những b&iacute; mật về cơ chế hoạt động của bộ m&aacute;y cơ thể người c&ugrave;ng c&aacute;ch thức n&oacute; sử dụng c&aacute;c vitamin v&agrave; kho&aacute;ng chất trong thực phẩm đang dần được đưa ra &aacute;nh s&aacute;ng. Những th&agrave;nh quả phục hồi sức khỏe khỏi hầu hết mọi loại bệnh tật v&agrave; sự x&aacute;o trộn ở cơ thể người gần như đ&atilde; trở th&agrave;nh một hiện tượng tr&ecirc;n to&agrave;n thế giới n&oacute;i chung v&agrave; trong lĩnh vực Trị liệu bằng Nước &eacute;p rau quả n&oacute;i ri&ecirc;ng. Trong thời đại ng&agrave;y nay, đ&aacute;ng tiếc thay cho người n&agrave;o c&ograve;n chưa được cung cấp đầy đủ th&ocirc;ng tin về những gi&aacute; trị dinh dưỡng v&agrave; c&ocirc;ng năng chữa l&agrave;nh của nước &eacute;p rau quả tươi sống.</p>\r\n', 0, 'Ryu Seung-SunVictoria Boutenko', '1629595555_combo-dinh-duong-than-duoc-xanh.jpg', '1629692122_c.jpg', 210000, 25, 28),
(52, 'Combo Ăn Xanh Để Khỏe - Sống Lành Để Trẻ', '', '<p>Kh&ocirc;ng bao giờ l&agrave; qu&aacute; muộn để bắt đầu trẻ h&oacute;a Bạn ăn thế n&agrave;o th&igrave; bạn sẽ trở th&agrave;nh thế ấy.</p>\r\n\r\n<p>Quả đ&uacute;ng như vậy. V&agrave; bạn cảm thấy m&igrave;nh trẻ (gi&agrave;) tới mức n&agrave;o, bạn sẽ trẻ (gi&agrave;) đ&uacute;ng ở mức đ&oacute;. Năm th&aacute;ng chỉ l&agrave; một phương tiện đ&aacute;nh dấu c&aacute;c mốc thời gian v&agrave; chẳng hề c&oacute; ch&uacute;t li&ecirc;n hệ n&agrave;o với tuổi t&aacute;c. Một người 30 tuổi c&oacute; thể đ&atilde; gi&agrave;, v&agrave; một người 70 tuổi c&oacute; khi vẫn trẻ.</p>\r\n\r\n<p>Thể trạng hiện tại l&agrave; hệ quả trực tiếp của to&agrave;n bộ sự chăm s&oacute;c cả về mặt tinh thần v&agrave; thể chất m&agrave; cơ thể nhận được trong qu&aacute; khứ.</p>\r\n\r\n<p>T&ocirc;i muốn nhấn mạnh sự chăm s&oacute;c về TINH THẦN trước ti&ecirc;n, bởi v&igrave; trạng th&aacute;i t&acirc;m tr&iacute; đ&oacute;ng vai tr&ograve; sống c&ograve;n đối với thể trạng của mọi con người. Một người kh&ocirc;ng thể c&oacute; sức khỏe nếu anh ta cứ li&ecirc;n tục nghĩ về bệnh tật; kh&ocirc;ng thể hạnh ph&uacute;c nếu t&acirc;m tr&iacute; lu&ocirc;n bị đ&aacute;m m&acirc;y u sầu che phủ; v&agrave; kh&ocirc;ng thể trẻ trung nếu lu&ocirc;n để bản th&acirc;n bị &aacute;m ảnh bởi sự t&agrave;n ph&aacute; của tuổi gi&agrave;. Trẻ trung tức l&agrave; tất cả hoặc phần lớn những thuộc t&iacute;nh của tuổi trẻ, sức khỏe, năng lượng, sinh kh&iacute;, v&agrave; nụ cười thường trực đều được thể hiện qua đ&ocirc;i mắt, v&agrave;nh m&ocirc;i. Trẻ trung nghĩa l&agrave; lu&ocirc;n chan h&ograve;a, th&acirc;n &aacute;i, lịch thiệp v&agrave; h&ograve;a nh&atilde; với tất cả mọi người, kh&ocirc;ng ph&acirc;n biệt t&iacute;n ngưỡng, m&agrave;u da hay địa vị x&atilde; hội. V&agrave; trẻ trung c&ograve;n đồng nghĩa với việc lu&ocirc;n giữ cho bản th&acirc;n ở trạng th&aacute;i năng động, lu&ocirc;n c&oacute; việc g&igrave; đ&oacute; để l&agrave;m, sao cho kh&ocirc;ng một gi&acirc;y một ph&uacute;t n&agrave;o bạn cảm thấy thời gian tr&ocirc;i qua thật l&ecirc; th&ecirc;, chậm chạp.</p>\r\n\r\n<p>Ở kh&iacute;a cạnh tinh thần, ch&uacute;ng ta phải nỗ lực lao động để thu được hoa tr&aacute;i l&agrave; SỰ TRẺ H&Oacute;A. Kh&iacute;a cạnh thể chất tuy c&oacute; đơn giản hơn song lại đ&ograve;i hỏi một &yacute; ch&iacute; sắt đ&aacute; v&agrave; một l&ograve;ng ki&ecirc;n định vững như b&agrave;n thạch. Trẻ h&oacute;a về mặt thể chất tức l&agrave; t&aacute;i x&acirc;y dựng v&agrave; t&aacute;i tạo lại cơ thể.</p>\r\n\r\n<p>C&ocirc;ng việc n&agrave;y thoạt ti&ecirc;n tưởng chừng qu&aacute; đỗi đơn giản nhưng tr&ecirc;n thực tế lại đ&ograve;i hỏi rất nhiều thời gian c&ugrave;ng một l&ograve;ng ki&ecirc;n nhẫn v&agrave; &yacute; ch&iacute; sắt đ&aacute;.</p>\r\n', 0, 'Norman W. Walker', '1629595644_combo-an-xanh-song-lanh.jpg', '1629691966_ba.jpg', 45000, 25, 28);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `author`, `photo`, `photo1`, `price`, `discount`, `category_id`) VALUES
(53, 'Combo Lược Sử Loài Người - Lược Sử Tương Lai - 21 Bài Học Cho Thế Kỷ 21', '', '<p><strong>Sapiens: Lược Sử Lo&agrave;i Người</strong></p>\r\n\r\n<p>Sapiens, đưa ch&uacute;ng ta v&agrave;o một chuyến đi kinh ngạc qua to&agrave;n bộ lịch sử lo&agrave;i người, từ những gốc rễ tiến h&oacute;a của n&oacute; đến thời đại của chủ nghĩa tư bản v&agrave; kỹ thuật di truyền, để kh&aacute;m ph&aacute; tại sao ch&uacute;ng ta đang trong những điều kiện sinh sống hiện tại.</p>\r\n\r\n<p>Sapiens tập trung v&agrave;o c&aacute;c qu&aacute; tr&igrave;nh quan trọng đ&atilde; định h&igrave;nh lo&agrave;i người v&agrave; thế giới quanh n&oacute;, chẳng hạn như sự ra đời của sản xuất n&ocirc;ng nghiệp, việc tạo ra tiền, sự lan truyền của những t&ocirc;n gi&aacute;o, v&agrave; sự nổi l&ecirc;n của những nh&agrave; nước quốc gia. Kh&ocirc;ng giống như những quyển s&aacute;ch kh&aacute;c c&ugrave;ng loại, Sapiens đ&atilde; c&oacute; một lối tiếp cận li&ecirc;n ng&agrave;nh học, bắc cầu qua những khoảng c&aacute;ch giữa lịch sử, sinh học, triết học v&agrave; kinh tế theo một lối trước đ&acirc;y chưa từng c&oacute;. Hơn nữa, lấy cả quan điểm vĩ m&ocirc; v&agrave; vi m&ocirc;, Sapiens kh&ocirc;ng chỉ đề cập đến những g&igrave; đ&atilde; xảy ra v&agrave; tại sao, m&agrave; c&ograve;n đi s&acirc;u v&agrave;o việc những c&aacute; nh&acirc;n trong lịch sử đ&oacute; đ&atilde; cảm nhận n&oacute; như thế n&agrave;o.</p>\r\n\r\n<p>C&acirc;u hỏi lớn v&agrave; s&acirc;u sắc của Harari l&agrave;: ch&uacute;ng ta thực sự muốn g&igrave;? C&oacute; c&aacute;ch n&agrave;o để đạt được hạnh ph&uacute;c cho con người ch&uacute;ng ta, hoặc thậm ch&iacute; liệu ch&uacute;ng ta c&oacute; biết được n&oacute; l&agrave; g&igrave; hay kh&ocirc;ng? Trong cốt l&otilde;i của n&oacute;, Sapiens biện luận rằng ch&uacute;ng ta kh&ocirc;ng biết về bản th&acirc;n ch&uacute;ng ta, huống chi biết được những nhu cầu của những lo&agrave;i sinh vật kh&aacute;c. Ch&uacute;ng ta đ&atilde; qu&aacute; thường xuy&ecirc;n bị những tưởng tượng hư cấu của ch&uacute;ng ta lừa dối. Lịch sử cũng l&agrave; một hư cấu, nhưng một hư cấu đ&atilde; được kiềm chế bởi thực tại v&agrave; biện luận: một h&igrave;nh thức của huyền thoại &ndash; một hư cấu hữu &iacute;ch &ndash; khiến n&oacute; c&oacute; thể mang lại sự gi&aacute;c ngộ của sự tự biết ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>&ldquo;T&ocirc;i khuyến kh&iacute;ch tất cả ch&uacute;ng ta, dẫu c&oacute; những tin tưởng t&iacute;n ngưỡng n&agrave;o, để đặt c&acirc;u hỏi về những thuật kể cơ bản về thế giới ch&uacute;ng ta, để nối những ph&aacute;t triển ng&agrave;y xưa với những quan t&acirc;m ng&agrave;y nay, v&agrave; để kh&ocirc;ng sợ h&atilde;i những vấn đề tranh luận&rdquo; (Yuval Noah Harari)</p>\r\n\r\n<h5>Homo Deus: Lược Sử Tương Lai</h5>\r\n\r\n<p>Homo sapiens c&oacute; phải l&agrave; một dạng sống si&ecirc;u đẳng, hay chỉ l&agrave; một tay đầu gấu địa phương? L&agrave;m thế n&agrave;o con người lại tin rằng họ kh&ocirc;ng chỉ đ&atilde; kiểm so&aacute;t thế giới, m&agrave; c&ograve;n mang lại &yacute; nghĩa cho n&oacute;? C&ocirc;ng nghệ sinh học v&agrave; tr&iacute; th&ocirc;ng minh nh&acirc;n tạo đe doạ lo&agrave;i người ra sao? Sinh vật n&agrave;o c&oacute; thể kế thừa lo&agrave;i người, v&agrave; t&ocirc;n gi&aacute;o mới n&agrave;o sẽ được sản sinh?</p>\r\n\r\n<h1>21 B&agrave;i Học Cho Thế Kỷ 21</h1>\r\n\r\n<p>Sau khi t&igrave;m hiểu qu&aacute; khứ v&agrave; tương lai của nh&acirc;n loại qua hai cuốn s&aacute;ch g&acirc;y tiếng vang l&agrave; Sapiens v&agrave; Homo deus, Yuval Noah Harari đi s&acirc;u v&agrave;o c&aacute;c vấn đề &ldquo;ngay tại đ&acirc;y&rdquo; v&agrave; &ldquo;ngay l&uacute;c n&agrave;y&rdquo;, tức c&aacute;c sự kiện hiện tại v&agrave; tương lai gần nhất của x&atilde; hội lo&agrave;i người.<br />\r\n<br />\r\nNhững triển vọng đầy hứa hẹn của c&ocirc;ng nghệ sẽ được đưa ra b&agrave;n luận b&ecirc;n cạnh những hiểm họa như &ldquo;đứt g&atilde;y&rdquo; do c&ocirc;ng nghệ g&acirc;y ra, việc kiểm so&aacute;t thế giới b&ecirc;n trong dẫn tới sự sụp đổ của hệ thần kinh hay &ldquo;tự do trong khu&ocirc;n khổ&rdquo;. Ch&iacute;nh trị v&agrave; t&ocirc;n gi&aacute;o c&oacute; c&ograve;n bắt tay nhau như trong qu&aacute; khứ hay sẽ thao t&uacute;ng con người theo những c&aacute;ch ri&ecirc;ng rẽ, mới mẻ hơn? V&agrave; những vấn đề to&agrave;n cầu ấy li&ecirc;n quan mật thiết tới h&agrave;nh vi v&agrave; đạo đức của từng c&aacute; nh&acirc;n ri&ecirc;ng lẻ như thế n&agrave;o? X&eacute;t cho c&ugrave;ng, những th&aacute;ch thức lớn nhất v&agrave; những lựa chọn quan trọng nhất của ng&agrave;y nay l&agrave; g&igrave;? Ta cần ch&uacute; &yacute; đến điều g&igrave;? Ta n&ecirc;n dạy con c&aacute;i ta những g&igrave;?<br />\r\n<br />\r\n&ldquo;Cuộc khủng hoảng sinh th&aacute;i đang lấp l&oacute;, mối đe dọa của c&aacute;c loại vũ kh&iacute; hủy diệt h&agrave;ng loạt [] v&agrave; sự trỗi dậy của c&aacute;c c&ocirc;ng nghệ đột ph&aacute; mới&rdquo; l&agrave; những nỗi lo kh&ocirc;ng của ri&ecirc;ng ai; v&agrave; Harari sẽ đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n con đường b&oacute;c t&aacute;ch từng vấn đề một c&aacute;ch thấu đ&aacute;o.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 0, 'Yuval Noah Harari', '1629595695_combo-luoc-su-loai-nguoi.jpg', '1629692075_bb.jpeg', 139000, 25, 24),
(54, 'Bộ Sách Phong Cách Sống (Bộ 5 Cuốn)', '', '<p>&quot;Kh&ocirc;ng qu&aacute; &iacute;t, kh&ocirc;ng qu&aacute; nhiều, chỉ vừa đủ&quot; l&agrave; cốt l&otilde;i của phong c&aacute;ch sống&nbsp;<strong>Lagom</strong>, nhưng biết như thế n&agrave;o l&agrave; đủ th&igrave; c&ograve;n phụ thuộc v&agrave;o giới hạn của mỗi người. Người Thụy Điển d&ugrave;ng Lagom như kim chỉ nam cho cả d&acirc;n tộc, h&igrave;nh th&agrave;nh n&ecirc;n phong c&aacute;ch sống, ứng xử giao tiếp v&agrave; thậm ch&iacute; cả ẩm thực của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p>L&agrave; một phần l&atilde;nh thổ của Bắc &Acirc;u, xứ sở nổi tiếng với phong c&aacute;ch sống tối giản, Thụy Điển tự x&acirc;y dựng cho m&igrave;nh một phong c&aacute;ch sống ho&agrave;n to&agrave;n kh&aacute;c, mang t&ecirc;n Lagom. Với t&agrave;i sản văn h&oacute;a ri&ecirc;ng n&agrave;y, người d&acirc;n Thụy Điển lu&ocirc;n biết vừa l&ograve;ng với những g&igrave; m&igrave;nh đang c&oacute; v&agrave; gần như kh&ocirc;ng bao giờ so s&aacute;nh với cuộc sống của người kh&aacute;c.</p>\r\n\r\n<p>&ldquo;Lagom&rdquo; l&agrave; một t&iacute;nh từ d&ugrave;ng để chỉ sự vừa phải, kh&ocirc;ng qu&aacute; nhiều m&agrave; cũng chẳng qu&aacute; &iacute;t. Thật kh&oacute; để c&oacute; thể chuyển tải hết &yacute; nghĩa của từ lagom sang bất kỳ thứ tiếng n&agrave;o kh&aacute;c. Người Thụy Điển d&ugrave;ng&nbsp;<strong>Lagom</strong>&nbsp;như &ldquo;kim chỉ nam&rdquo; chung cho cả d&acirc;n tộc, h&igrave;nh th&agrave;nh n&ecirc;n phong c&aacute;ch sống, ứng xử, giao tiếp, v&agrave; thậm ch&iacute; cả ẩm thực của ri&ecirc;ng m&igrave;nh. Lagom l&agrave; vừa đủ, l&agrave; th&iacute;ch hợp, l&agrave; c&acirc;n bằng nhưng kh&ocirc;ng nhất thiết phải l&agrave; ho&agrave;n hảo nhất. Như một giai thoại từ thế kỷ trước, nhiều người tin rằng từ n&agrave;y bắt nguồn từ thuật ngữ &quot;laget om&quot; của người Viking - nghĩa l&agrave; &quot;chuyền quanh trong nh&oacute;m&rdquo; xuất ph&aacute;t từ th&oacute;i quen những người Viking. V&agrave;o thời đ&oacute;, khi tất cả c&ugrave;ng ngồi quanh chiếc b&agrave;n v&agrave; chuyền tay nhau những chiếc sừng đựng đầy rượu, mỗi người nhấp một ngụm vừa phải, l&agrave;m sao để những người sau đ&oacute; cũng c&oacute; đủ rượu để thưởng thức.</p>\r\n\r\n<p>Trong một x&atilde; hội m&agrave; con người c&oacute; thể tiếp cận với bất cứ thứ g&igrave;, v&agrave;o bất cứ l&uacute;c n&agrave;o th&igrave; Lagom giống như một cơn gi&oacute; lạ. Sự h&agrave;i l&ograve;ng trong phong c&aacute;ch sống của người Thụy Điển kh&ocirc;ng phải l&agrave; v&igrave; họ c&oacute; tất cả mọi thứ, m&agrave; đơn giản đ&oacute; l&agrave; một trạng th&aacute;i thỏa m&atilde;n với những g&igrave; m&igrave;nh đang c&oacute;. Lagom ch&iacute;nh l&agrave; việc tho&aacute;t ra khỏi những bộn bề v&agrave; c&acirc;n đo đong đếm một c&aacute;ch th&aacute;i qu&aacute;.</p>\r\n\r\n<p><strong>Tinh thần Lagom</strong>&nbsp;thể hiện ở mọi mặt của cuộc sống Thụy Điển, từ việc chỉ n&oacute;i ngắn gọn vừa đủ kh&ocirc;ng khoa trương, ở trong những ng&ocirc;i nh&agrave; đơn giản &iacute;t đồ đạc v&agrave; tho&aacute;ng đ&atilde;ng, ăn những đồ ăn kh&ocirc;ng qu&aacute; cầu kỳ, thậm ch&iacute; đến vui chơi cũng kh&ocirc;ng qu&aacute; ồn &agrave;o, n&aacute;o nhiệt&hellip; Tất cả những điều đ&oacute; đ&atilde; tạo n&ecirc;n một phong c&aacute;ch sống tối giản nhưng mang lại sự b&igrave;nh an trong t&acirc;m hồn v&agrave; h&agrave;i h&ograve;a trong cuộc sống.</p>\r\n\r\n<p>Mang tập s&aacute;ch đến bạn đọc Việt Nam, mong rằng ch&uacute;ng ta đ&oacute;n nhận Lagom với một tinh thần v&agrave; lối sống mới, biết vừa đủ với tất cả để cuộc sống bớt lại những g&aacute;nh nặng, bộn bề. Từ đ&oacute; bạn sẽ t&igrave;m được những b&agrave;i học v&agrave; lời khuy&ecirc;n để bắt đầu một lối sống Lagom. Đ&oacute; c&oacute; thể l&agrave; những b&agrave;i học về sự tiết chế v&agrave; c&acirc;n bằng, t&igrave;m sự c&acirc;n bằng trong cuộc sống thường nhật để ch&uacute;ng ta c&oacute; thể t&igrave;m được niềm vui tốt đẹp hơn, để từ đ&oacute; c&oacute; được những lợi &iacute;ch cả về thể chất lẫn tinh thần cho bản th&acirc;n, cũng như để cộng đồng v&agrave; m&ocirc;i trường của ch&uacute;ng ta được c&acirc;n bằng hơn nhờ v&agrave;o lối sống của ch&uacute;ng ta.</p>\r\n\r\n<p>&quot;Kh&ocirc;ng qu&aacute; &iacute;t, kh&ocirc;ng qu&aacute; nhiều, chỉ vừa đủ&quot; nhưng biết như thế n&agrave;o l&agrave; đủ th&igrave; đ&oacute; lại l&agrave; giới hạn của mỗi người. Chỉ biết rằng, với người Thụy Điển, họ lu&ocirc;n biết c&aacute;ch l&agrave;m h&agrave;i l&ograve;ng bản th&acirc;n bằng những cột mốc ri&ecirc;ng để l&agrave;m sao vẫn đủ đầy cho bản th&acirc;n nhưng kh&ocirc;ng bị th&aacute;i qu&aacute;. &Iacute;t hơn, để c&oacute; được nhiều hơn l&agrave; vậy.&nbsp;</p>\r\n\r\n<p><strong>2.&nbsp;</strong><em><strong><strong>H</strong>ugge - Hạnh Ph&uacute;c Từ Những Điều Nhỏ B&eacute; - Nghệ Thuật Sống Của Người Đan Mạch</strong></em></p>\r\n\r\n<p><strong>Cuốn s&aacute;ch giải m&atilde; b&iacute; mật gi&uacute;p con người sống ở quốc gia c&oacute; hơn một nửa thời gian của năm ch&igrave;m trong mưa x&aacute;m v&agrave; tuyết lạnh hạnh ph&uacute;c.</strong></p>\r\n\r\n<p><strong>&nbsp;</strong>Mỗi quốc gia c&oacute; những gi&aacute; trị cốt l&otilde;i kh&aacute;c nhau để l&agrave;m nền tảng cho việc hiểu v&agrave; định h&igrave;nh đặc điểm d&acirc;n tộc m&igrave;nh. Người Mỹ y&ecirc;u th&iacute;ch tự do c&aacute; nh&acirc;n, người Ph&aacute;p c&oacute; danh vọng, c&ograve;n người Đức đề cao t&iacute;nh kỷ luật v&agrave; sự ch&iacute;nh x&aacute;c. Đối với người Đan Mạch,&nbsp;<em>Hygge</em>&nbsp;l&agrave; một trong những gi&aacute; trị cốt l&otilde;i. Đ&acirc;y l&agrave; nền tảng mang lại hạnh ph&uacute;c cho quốc gia c&oacute; hơn một nửa thời gian của năm ch&igrave;m trong mưa x&aacute;m v&agrave; tuyết lạnh. Trong bối cảnh đời sống qu&aacute; nhiều &aacute;p lực, giải m&atilde; những &ldquo;b&iacute; mật&rdquo; của Hygge để c&oacute; thể ứng dụng v&agrave;o đời sống đang l&agrave; điều rất nhiều người hướng đến.&nbsp;<strong><em>Hạnh ph&uacute;c từ những điều nhỏ b&eacute;,</em></strong>&nbsp;cuốn s&aacute;ch của t&aacute;c giả Marie Tourell S&oslash;derberg c&oacute; chứa ch&igrave;a kh&oacute;a để mở c&aacute;nh cửa v&agrave;o căn ph&ograve;ng chứa mọi lời giải đ&aacute;p về&nbsp;<em>Hygge</em>.</p>\r\n\r\n<p>Marie Tourell S&oslash;derberg kh&ocirc;ng phải l&agrave; một người viết s&aacute;ch chuy&ecirc;n nghiệp. C&ocirc; l&agrave; một diễn vi&ecirc;n sống ở Copenhagen. Cũng như đa số mọi người ở thời đại n&agrave;y, đ&ocirc;i khi Marie cảm thấy qu&aacute; tải với c&ocirc;ng việc, thấy m&igrave;nh lu&ocirc;n bận rộn với nhiệm vụ n&agrave;o đ&oacute; đến mức qu&ecirc;n d&agrave;nh thời gian để bu&ocirc;ng bỏ mọi vướng bận v&agrave; để bản th&acirc;n thật sự được sống. Cho đến một ng&agrave;y, Marie nhận ra, m&igrave;nh, cũng như rất nhiều người đang bị v&ograve;ng xo&aacute;y c&ocirc;ng việc chi phối qu&aacute; nhiều. Marie quyết định chậm lại, bởi với c&ocirc;, việc c&oacute; khoảng thời gian&nbsp;<em>hygge</em>&nbsp;c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave;, c&oacute; những khoảnh khắc&nbsp;<em>hygge</em>&nbsp;với bản th&acirc;n v&agrave; c&oacute; một m&aacute;i ấm hygge l&agrave; điều rất quan trọng. Marie bắt tay v&agrave;o viết về&nbsp;<em>Hygge</em>&nbsp;với mong muốn quyển s&aacute;ch sẽ l&agrave; một t&agrave;i liệu hướng dẫn thực h&agrave;nh gi&uacute;p mọi người x&acirc;y dựng nền tảng vững chắc để ph&aacute;t triển hygge. Đồng thời cũng l&agrave; lời mời mọi người tận hưởng v&agrave; tr&acirc;n trọng những khoảnh khắc kh&ocirc;ng thể đ&aacute;nh đổi bằng tiền.</p>\r\n\r\n<p>Theo Gi&aacute;o sư Jeppe Trolle Linnet, chuy&ecirc;n gia nghi&ecirc;n cứu về&nbsp;<em>Hygge, Hygge</em>&nbsp;l&agrave; một từ trong tiếng Đan Mạch, h&agrave;m chứa to&agrave;n bộ&nbsp; c&aacute;c &yacute; nghĩa m&agrave; con người ch&uacute;ng ta v&ocirc; c&ugrave;ng tr&acirc;n trọng.&nbsp;<em>Hygge</em>&nbsp;trong ng&ocirc;n ngữ Bắc &Acirc;u cổ l&agrave;&nbsp;<em>hyggja</em>, c&oacute; nghĩa l&agrave; sự thỏa m&atilde;n trong suy nghĩ v&agrave; cảm nhận. Đồng thời, c&oacute; li&ecirc;n quan đến việc t&igrave;m nơi tr&uacute; ẩn, nghỉ ngơi, sự an to&agrave;n v&agrave; phục hồi năng lượng cũng như l&ograve;ng can đảm. Đối với nhiều người Đan Mạch,&nbsp;<em>hygge</em>&nbsp;l&agrave; mục ti&ecirc;u cần phấn đấu đạt được. N&oacute; như kim chỉ nam dẫn ch&uacute;ng ta đến những khoảnh khắc hạnh ph&uacute;c nho nhỏ trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>X&aacute;c định cuốn s&aacute;ch sẽ l&agrave; một t&agrave;i liệu hướng dẫn thực h&agrave;nh,&nbsp;<strong><em>Hạnh ph&uacute;c từ những điều nhỏ b&eacute;</em></strong>&nbsp;được t&aacute;c giả chắt lọc, đầu tư từ c&acirc;u chữ đến h&igrave;nh ảnh. Marie đưa người đọc v&agrave;o từng ng&ocirc;i nh&agrave;, từng gian bếp, từng g&oacute;c ph&ograve;ng&hellip; để chứng kiến nhịp sinh hoạt đặc biệt của những con người nơi đ&acirc;y. Marie đ&atilde; đ&agrave;nh kh&ocirc;ng &iacute;t thời gian để gặp gỡ, trao đổi với rất nhiều th&agrave;nh phần trong x&atilde; hội, từ c&aacute;c chuy&ecirc;n gia nghi&ecirc;n cứu về Hygge, diễn giả, nh&agrave; t&acirc;m l&yacute;&hellip; để lắng nghe những định nghĩa của họ về&nbsp;<em>Hygge</em>&nbsp;cũng như c&aacute;ch họ thiết lập tinh thần n&agrave;y v&agrave;o ch&iacute;nh cuộc sống thường nhật. Độc giả sẽ được xu&ocirc;i theo d&ograve;ng lịch sử, địa l&yacute;, con người&hellip; Đan Mạch để hiểu v&igrave; sao kh&aacute;i niệm&nbsp;<em>Hygge</em>&nbsp;được khởi ph&aacute;t v&agrave; để thấy được t&aacute;c dụng của n&oacute; thế n&agrave;o, cả trong qu&aacute; khứ lẫn hiện tại với quốc gia n&agrave;y.</p>\r\n\r\n<p>Kh&ocirc;ng dừng lại ở những kh&aacute;i niệm, t&aacute;c giả cũng đưa ra cụ thể c&aacute;ch thức triển khai, thực h&agrave;nh để l&agrave;m thế n&agrave;o đưa&nbsp;<em>Hygge</em>&nbsp;v&agrave;o nh&agrave;, v&agrave;o ch&iacute;nh đời sống thường nhật để cuộc sống mỗi người c&oacute; thể trở n&ecirc;n tốt đẹp hơn. Xuy&ecirc;n suốt quyển s&aacute;ch, người đọc sẽ được rất nhiều người Đan Mạch giải th&iacute;ch về việc họ&nbsp;<em>hygge</em>&nbsp;như thế n&agrave;o v&agrave;&nbsp;<em>hygge</em>&nbsp;c&oacute; &yacute; nghĩa g&igrave; với họ: Hygge l&agrave; ngồi quanh đống lửa tr&ograve; chuyện với bạn b&egrave; trong một đ&ecirc;m h&egrave;; Hygge l&agrave; nh&acirc;m nhi ly rượu vang trong khu vườn của mẹ; Hygge l&agrave; ngồi nghe tiếng mưa rơi t&iacute; t&aacute;ch tr&ecirc;n m&aacute;i nh&agrave; với một t&aacute;ch tr&agrave; n&oacute;ng tr&ecirc;n tay&hellip;.</p>\r\n\r\n<p>Chẳng c&oacute; định nghĩa chung, thống nhất nhưng sau to&agrave;n bộ nội dung t&aacute;c phẩm, người đọc sẽ cảm nhận được tinh thần của Hygge v&agrave; tự chọn được c&aacute;ch&nbsp;<em>Hygge</em>&nbsp;cho m&igrave;nh. Như lời của Anne Glad, chuy&ecirc;n gia h&agrave;ng đầu thế giới về lối sống, c&oacute; thể&nbsp;<em>hygge</em>&nbsp;kh&ocirc;ng phải l&agrave; đ&aacute;p &aacute;n cho mọi c&acirc;u hỏi lớn trong cuộc sống, nhưng chắc chắn&nbsp;<em>hygge</em>&nbsp;l&agrave; một lời mời gọi ch&uacute;ng ta suy nghĩ về cuộc sống v&agrave; hạnh ph&uacute;c dưới một g&oacute;c nh&igrave;n rộng hơn. Điều g&igrave; l&agrave; quan trọng đối với ch&uacute;ng ta? Ch&uacute;ng ta cần l&agrave;m việc nhiều đến mức n&agrave;o? V&agrave; ch&uacute;ng ta cần g&igrave; để hạnh ph&uacute;c?&nbsp;<em>Hygge</em>&nbsp;l&agrave; lời mời gọi tận hưởng những thứ nhỏ b&eacute; trong cuộc sống, bởi v&igrave; thật ra đ&oacute; ch&iacute;nh l&agrave; những điều to lớn.</p>\r\n\r\n<p><em><strong>3. Sisu - Vượt Qua Tất Cả - Nghệ Thuật Sống Của Người Phần Lan</strong></em></p>\r\n\r\n<p>Sisu l&agrave; một từ kh&oacute; diễn giải trong tiếng Phần Lan v&agrave; kh&ocirc;ng c&oacute; từ tiếng Anh tương đương. Thuật ngữ n&agrave;y l&agrave; sự pha trộn của l&ograve;ng can đảm, sự dẻo dai, tinh thần bền bỉ v&agrave; t&iacute;nh ki&ecirc;n tr&igrave; &ndash; những phẩm chất gi&uacute;p định h&igrave;nh nghệ thuật sống &quot;vượt qua tất cả&quot; của con người v&agrave; đất nước Phần Lan. Tinh thần sisu kh&ocirc;ng chỉ tồn tại trong tr&aacute;i tim mỗi người d&acirc;n của xứ sở n&agrave;y, m&agrave; c&ograve;n được thể hiện trong cuộc sống thường nhật nơi đ&acirc;y.</p>\r\n\r\n<p>&nbsp;L&agrave; một quốc gia nhỏ b&eacute; ở v&ugrave;ng Bắc &Acirc;u, nổi tiếng với thời tiết v&agrave; m&ocirc;i trường thi&ecirc;n nhi&ecirc;n khắc nghiệt, nhưng Phần Lan chưa bao giờ rời khỏi nh&oacute;m những quốc gia c&oacute; chỉ số hạnh ph&uacute;c cao nhất thế giới. Đối với người d&acirc;n Phần Lan, &quot;d&agrave;nh thời gian h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n&quot; l&agrave; một trong những yếu tố h&agrave;ng đầu l&agrave;m n&ecirc;n hạnh ph&uacute;c, b&ecirc;n cạnh sự độc lập, b&igrave;nh y&ecirc;n, trật tự, hữu dụng v&agrave; sự c&ocirc;ng bằng. V&agrave; tất cả những yếu tố n&agrave;y đều c&oacute; thể được g&oacute;i gọn trong một b&iacute; quyết mang t&ecirc;n&nbsp;<em>sisu</em>&nbsp;&ndash; tinh thần vượt qua tất cả.</p>\r\n\r\n<p>Thật kh&oacute; định nghĩa ch&iacute;nh x&aacute;c&nbsp;<em>sisu</em>&nbsp;l&agrave; g&igrave;. Kh&ocirc;ng c&oacute; một từ tiếng Anh n&agrave;o c&oacute; &yacute; nghĩa tương đương với&nbsp;<em>sisu</em>, v&agrave; ngay cả trong tiếng Phần Lan th&igrave;&nbsp;<em>sisu</em>&nbsp;cũng đại diện cho một tổ hợp nhiều đặc điểm kh&aacute;c nhau, bao gồm quyết t&acirc;m sắt đ&aacute;, nghị lực, l&ograve;ng can đảm, sự dũng cảm, &yacute; ch&iacute; bền bỉ, sự ngoan cường v&agrave; ki&ecirc;n tr&igrave;. Tinh thần&nbsp;<em>sisu</em>&nbsp;sẽ thức tỉnh khi những kh&oacute; khăn v&agrave; nghịch cảnh khiến bạn muốn bỏ cuộc, khi m&agrave; chỉ c&oacute; dũng kh&iacute; mới c&oacute; thể gi&uacute;p bạn tiếp tục tiến l&ecirc;n. Nhưng sẽ chẳng c&oacute; &yacute; nghĩa g&igrave; nếu bạn n&oacute;i rằng m&igrave;nh c&oacute; tinh thần sisu nhưng kh&ocirc;ng thể hiện được tinh thần đ&oacute;.&nbsp;<em>Sisu</em>&nbsp;l&agrave; lối tư duy hướng đến h&agrave;nh động, chứ kh&ocirc;ng phải chỉ l&agrave; tỏ ra can đảm.</p>\r\n\r\n<p>Trong một x&atilde; hội m&agrave; con người phải đối mặt với v&ocirc; v&agrave;n thử th&aacute;ch để sinh tồn v&agrave; ph&aacute;t triển th&igrave;&nbsp;<em>sisu</em>&nbsp;l&agrave; thiết yếu.&nbsp;<em>Sisu</em>&nbsp;kh&ocirc;ng phải l&agrave; nguồn sức mạnh gi&uacute;p ta chạy một mạch l&ecirc;n đỉnh n&uacute;i, m&agrave;&nbsp;<em>sisu</em>&nbsp;l&agrave; nguồn lực xuất hiện khi nguồn năng lượng m&agrave; ta nhận thức được đang dần cạn kiệt, khi nghịch cảnh k&eacute;o đến v&agrave; ta dường như kh&ocirc;ng c&ograve;n lối tho&aacute;t. Ch&iacute;nh v&agrave;o l&uacute;c đ&oacute;,&nbsp;<em>sisu</em>&nbsp;sẽ trỗi dậy để gi&uacute;p ta kh&ocirc;ng ngừng đặt từng bước ch&acirc;n l&ecirc;n con đường ph&iacute;a trước. Kh&aacute;c với những chiến c&ocirc;ng hoặc h&agrave;nh động xuất ph&aacute;t từ sự liều lĩnh hay chủ nghĩa anh h&ugrave;ng cực đoan, những chiến t&iacute;ch của tinh thần&nbsp;<em>sisu</em>&nbsp;lu&ocirc;n diễn ra trong thầm lặng.</p>\r\n\r\n<p>Tinh thần&nbsp;<em>sisu</em>&nbsp;thể hiện ở mọi mặt của cuộc sống Phần Lan, từ việc chuẩn bị thật kỹ lưỡng để đương đầu với những kh&oacute; khăn thử th&aacute;ch, mạnh mẽ thừa nhận những điểm yếu của bản th&acirc;n, d&agrave;nh thời gian để im lặng suy nghĩ, n&oacute;i những lời trung thực v&agrave; thẳng thắn chứ kh&ocirc;ng hoa mỹ, t&ocirc;n thờ sự b&igrave;nh đẳng v&agrave; c&ocirc;ng bằng, cho đến những chuyến đi bộ đường d&agrave;i trong rừng, tận hưởng m&ugrave;a h&egrave; trong những ng&ocirc;i nh&agrave; gỗ nhỏ b&eacute; ở v&ugrave;ng hẻo l&aacute;nh thốn thiếu tiện nghi v&agrave; đi tắm hồ v&agrave;o giữa m&ugrave;a đ&ocirc;ng lạnh gi&aacute; Tất cả những điều đ&oacute; đ&atilde; tạo n&ecirc;n một phong c&aacute;ch sống b&igrave;nh dị m&agrave; ki&ecirc;n cường, h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n nhưng cũng v&ocirc; c&ugrave;ng hiện đại.</p>\r\n\r\n<p>Mang tập s&aacute;ch&nbsp;<strong><em>Vượt qua tất cả - Nghệ thuật sống của người Phần Lan</em></strong>&nbsp;đến với bạn đọc Việt Nam, ch&uacute;ng t&ocirc;i mong rằng tất cả ch&uacute;ng ta sẽ đ&oacute;n nhận&nbsp;<em>Sisu</em>&nbsp;v&agrave; vững v&agrave;ng đối mặt với những thử th&aacute;ch trong cuộc sống. Để rồi từ đ&oacute; bạn sẽ t&igrave;m được những b&agrave;i học v&agrave; lời khuy&ecirc;n để bắt đầu tận hưởng cuộc sống n&agrave;y, để t&igrave;m thấy hạnh ph&uacute;c v&agrave; niềm vui trong ngay cả những thời khắc kh&oacute; khăn nhất. Bởi lẽ&nbsp;<em>sisu</em>&nbsp;l&agrave; một đặc điểm mang t&iacute;nh phổ qu&aacute;t. C&oacute; thể người Phần Lan đ&atilde; gọi t&ecirc;n n&oacute;, định nghĩa n&oacute;, nhưng tinh thần&nbsp;<em>sisu</em>&nbsp;&ndash; tinh thần vượt qua tất cả &ndash; kh&ocirc;ng hề giới hạn ở đất nước hay con người Phần Lan, m&agrave; bất kỳ ai tr&ecirc;n thế giới n&agrave;y cũng c&oacute; thể nắm bắt v&agrave; vận dụng&nbsp;<em>sisu</em>.&nbsp;<em>Sisu</em>&nbsp;lu&ocirc;n hiện diện trong bạn, v&agrave; c&oacute; lẽ bạn cũng từng vận dụng&nbsp;<em>sisu</em>&nbsp;rồi đấy.</p>\r\n\r\n<p>&nbsp;<strong><em>[Sisu] Vượt qua tất cả - Nghệ thuật sống của người Phần Lan</em></strong>&nbsp;đ&atilde; bổ sung v&agrave;o bộ s&aacute;ch&nbsp;<em>Phong c&aacute;ch sống Bắc &Acirc;u</em>&nbsp;do First News thực hiện (c&ugrave;ng với&nbsp;<strong><em>[Lagom] Vừa đủ - Đẳng cấp sống của người Thụy Điển</em></strong>&nbsp;v&agrave;&nbsp;<strong><em>[Hygge]</em></strong>&nbsp;<strong><em>Hạnh ph&uacute;c từ những điều nhỏ b&eacute;</em></strong>&nbsp;-&nbsp;<strong><em>Nghệ thuật sống của người Đan Mạch.&nbsp;</em></strong>Mời bạn đ&oacute;n đọc.</p>\r\n\r\n<p><strong>4. Ikigai - Chất Nhật Trong Từng Khoảnh Khắc</strong></p>\r\n\r\n<p>Ikigai l&agrave; một kh&aacute;i niệm đ&atilde; tồn tại từ rất l&acirc;u trong truyền thống văn h&oacute;a Nhật Bản. Đ&acirc;y l&agrave; kh&aacute;i niệm về niềm vui trong cuộc sống thường nhật, hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; l&yacute; do m&agrave; bạn thức dậy v&agrave;o mỗi s&aacute;ng.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch,&nbsp;<em>Yukari Mitsuhashi</em>&nbsp;đ&atilde; phỏng vấn rất nhiều người, bao gồm cả nh&agrave; khởi nghiệp hay vận động vi&ecirc;n để hỏi về những thứ m&agrave; ikigai đ&atilde; đem đến cho họ, n&oacute; đ&atilde; gi&uacute;p họ vượt qua những kh&oacute; khăn trong cuộc sống như thế n&agrave;o, v&agrave; bằng c&aacute;ch n&agrave;o m&agrave; họ t&igrave;m ra ikigai của ch&iacute;nh m&igrave;nh. Thực tế cho thấy mỗi người c&oacute; một ikigai kh&aacute;c nhau v&agrave; kh&ocirc;ng phải ai cũng đ&atilde; t&igrave;m ra được ikigai cho bản th&acirc;n m&igrave;nh.</p>\r\n\r\n<p><strong>Ikigai - Chất Nhật trong từng khoảnh khắc</strong>&nbsp;l&agrave; cuốn s&aacute;ch tập trung v&agrave;o việc t&igrave;m kiếm ikigai của bản th&acirc;n, x&aacute;c định mục ti&ecirc;u hoặc đam m&ecirc; của bạn v&agrave; sử dụng sự hiểu biết n&agrave;y để đạt được niềm hạnh ph&uacute;c to lớn hơn trong đời. D&ugrave; bạn đang ở độ tuổi n&agrave;o nếu bạn t&igrave;m được ikigai dưới dạng sở th&iacute;ch hoặc c&aacute;c hoạt động kh&aacute;c n&oacute; sẽ gi&uacute;p bạn giữ được sự năng động v&agrave; t&igrave;nh y&ecirc;u đối với cuộc sống.</p>\r\n\r\n<p>T&ugrave;y v&agrave;o ikigai của bạn l&agrave; g&igrave; m&agrave; bạn sẽ nhận được những điều kh&aacute;c nhau từ n&oacute;, nhưng nh&igrave;n chung hiểu về ikigai của bản th&acirc;n sẽ gi&uacute;p bạn cảm thấy hạnh ph&uacute;c v&agrave; h&agrave;i l&ograve;ng về bản th&acirc;n m&igrave;nh, c&oacute; t&acirc;m tr&iacute; điềm tĩnh v&agrave; vững v&agrave;ng hơn, kiểm so&aacute;t cuộc sống thường nhật tốt hơn, t&igrave;m thấy được mục đ&iacute;ch sống, chủ động hơn v&agrave; gi&uacute;p bạn t&igrave;m thấy nguồn sinh lực để sống v&agrave; ph&aacute;t triển. Tất cả những điều n&agrave;y c&oacute; được l&agrave; nhờ bạn hiểu bản th&acirc;n muốn g&igrave; v&agrave; tr&acirc;n trọng điều g&igrave; trong cuộc sống.</p>\r\n\r\n<p>Cuộc sống lu&ocirc;n lu&ocirc;n đem đến cho ch&uacute;ng ta những kh&oacute; khăn ri&ecirc;ng, v&agrave; chắc chắn ikigai kh&ocirc;ng phải l&agrave; một c&ocirc;ng thức kỳ diệu gi&uacute;p mọi thứ trở n&ecirc;n ho&agrave;n hảo. Nhưng khi c&oacute; ikigai tr&ecirc;n từng chặng đường, ch&uacute;ng ta c&oacute; thể m&atilde;n nguyện khi nh&igrave;n lại cả cuộc đời m&igrave;nh.</p>\r\n\r\n<p>Với triết l&yacute; đơn giản v&agrave; quan điểm ph&oacute;ng kho&aacute;ng&nbsp;<strong>Ikigai - Chất Nhật trong từng khoảnh khắc&nbsp;</strong>sẽ l&agrave; nguồn cảm hứng gi&uacute;p bạn theo đuổi hạnh ph&uacute;c v&agrave; quan trọng hơn l&agrave; hiểu được ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p><strong>5.&nbsp;Sống Thanh Thản Như Người Thụy Điển</strong></p>\r\n\r\n<p>Việc dọn dẹp nh&agrave; cửa dường như chỉ tất bật v&agrave; hối hả trong những ng&agrave;y cận tết. Thế nhưng, kh&ocirc;ng phải ri&ecirc;ng dịp đầu năm, kh&ocirc;ng gian sống mới cần gọn g&agrave;ng, ngăn nắp. Khi bạn kh&ocirc;ng k&eacute;o nổi hộc tủ hay chẳng thể đ&oacute;ng cửa kho chứa đồ, đấy l&agrave; l&uacute;c ng&ocirc;i nh&agrave; b&aacute;o hiệu rằng bạn cần một cuộc dọn dẹp.</p>\r\n\r\n<p>Với người Thụy Điển, dọn dẹp kh&ocirc;ng hẳn l&agrave; phải vứt bỏ hầu hết đồ đạc, m&agrave; l&agrave; tinh gọn cuộc sống để bạn c&oacute; thể giữ lại những g&igrave; thật sự mang đến niềm vui cho m&igrave;nh v&agrave; cho những người th&acirc;n y&ecirc;u. Họ c&oacute; hẳn một thuật ngữ d&agrave;nh cho việc n&agrave;y: D&ouml;st&auml;dning.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch&nbsp;<strong>Sống Thanh Thản Như Người Thụy Điển</strong>, bằng lời văn nhẹ nh&agrave;ng, h&oacute;m hỉnh, c&acirc;y b&uacute;t Margareta Magnusson sẽ gi&uacute;p người đọc nh&igrave;n lại ng&ocirc;i nh&agrave; một c&aacute;ch chậm r&atilde;i, t&igrave;m ra thứ m&igrave;nh muốn giữ b&ecirc;n cạnh v&agrave; dọn dẹp những m&oacute;n đồ kh&ocirc;ng c&ograve;n ph&ugrave; hợp. Một v&agrave;i bộ quần &aacute;o c&oacute; thể đến với Hội Chữ thập đỏ, những chậu hoa c&oacute; thể được dọn đến nh&agrave; ai đ&oacute; sưởi nắng, kể cả kho dụng lặt vặt b&aacute;m bụi từ l&acirc;u cũng c&oacute; thể t&igrave;m người chủ kh&aacute;c&hellip;</p>\r\n\r\n<p>Kh&ocirc;ng chỉ ch&uacute; &yacute; đến chuyện tinh giản đồ đạc để xếp gọn cuộc sống, trong văn h&oacute;a Thụy Điển, cụm từ &ldquo;d&ouml;st&auml;dning&rdquo; c&ograve;n chỉ qu&aacute; tr&igrave;nh dọn dẹp nh&agrave; cửa để chuẩn bị cho sự ra đi khi tuổi của ch&uacute;ng ta đ&atilde; cao. Ch&iacute;nh v&igrave; vậy, d&ouml;st&auml;dning kh&ocirc;ng hẳn tập trung ho&agrave;n to&agrave;n v&agrave;o đồ vật. Qua cuốn s&aacute;ch, t&aacute;c giả Margareta đ&atilde; vực dậy l&ograve;ng can đảm ở người đọc: D&aacute;m đối diện với những kỷ vật xưa cũ, sẵn s&agrave;ng bu&ocirc;ng bỏ ch&uacute;ng để t&igrave;m kiếm niềm hạnh ph&uacute;c mới v&agrave; để lại sự thanh thản cho những người ở lại.</p>\r\n\r\n<p>Vượt qua bản chất của một thuật ngữ địa phương, ẩn b&ecirc;n trong &ldquo;D&ouml;st&auml;dning&rdquo; l&agrave; một lối sống văn minh, c&oacute; tr&aacute;ch nhiệm với ch&iacute;nh bản th&acirc;n v&agrave; những người xung quanh. Từ những chỉ dẫn trong&nbsp;<strong>Sống Thanh Thản Như Người Thụy Điển</strong>, bạn c&oacute; thể thực h&agrave;nh d&ouml;st&auml;dning ở bất cứ giai đoạn n&agrave;o của cuộc đời, bởi lẽ d&ugrave; trong thời điểm n&agrave;o đi chăng nữa, một m&aacute;i nh&agrave; ngăn nắp sẽ lu&ocirc;n mang đến những điều t&iacute;ch cực cho t&acirc;m hồn bạn.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, .....</p>\r\n', 0, 'Marie Tourell Soderberg', '1629595765_combo-phong-cach-song.jpg', '1629692038_aa.jpg', 554000, 25, 21),
